import{_ as e,o as i,c as n,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 54.1-1128 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-1128.md","filePath":"vacode/54.1-1128.md"}'),a={name:"vacode/54.1-1128.md"},r=t('<h1 id="§-54-1-1128-definitions" tabindex="-1">§ 54.1-1128 Definitions. <a class="header-anchor" href="#§-54-1-1128-definitions" aria-label="Permalink to &quot;§ 54.1-1128 Definitions.&quot;">​</a></h1><p>&quot;Backflow prevention device worker&quot; means any individual who engages in, or offers to engage in, the maintenance, repair, testing, or periodic inspection of cross connection control devices, including but not limited to reduced pressure principle backflow preventors, double check-valve assemblies, double-detector check-valve assemblies, pressure type vacuum breaker assemblies, and other such devices designed, installed, and maintained in such a manner so as to prevent the contamination of the potable water supply by the introduction of nonpotable liquids, solids, or gases, thus ensuring that the potable water supply remains unaltered and free from impurities, odor, discoloration, bacteria, and other contaminants which would make the potable water supply unfit or unsafe for consumption and use.</p><p>&quot;Board&quot; means the Board for Contractors.</p><p>&quot;Liquefied petroleum gas fitter&quot; means any individual who engages in, or offers to engage in, work for the general public for compensation in work that includes the installation, repair, improvement, alterations or removal of piping, liquefied petroleum gas tanks and appliances (excluding hot water heaters, boilers and central heating systems which require a heating, ventilation and air conditioning or plumbing certification) annexed to real property.</p><p>&quot;Natural gas fitter provider&quot; means any individual who engages in or offers to engage in work for the general public for compensation in the incidental repair, testing, or removal of natural gas piping or fitting annexed to real property, excluding new installation of gas piping for hot water heaters, boilers, central heating systems, or other natural gas equipment which requires heating, ventilation and air conditioning or plumbing certification.</p><p>&quot;Tradesman&quot; means any individual who engages in, or offers to engage in, work for the general public for compensation in the trades of electrical, plumbing and heating, ventilation and air conditioning.</p><p>&quot;Water well systems provider&quot; means any individual who is certified by the Board in accordance with this article and who is engaged in drilling, installation, maintenance, or repair of water wells, water well pumps, ground source heat exchangers, and other equipment associated with the construction, removal, or repair of water wells, water well systems, and ground source heat pump exchangers to the point of connection to the ground source heat pump.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0895" target="_blank" rel="noreferrer">895</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0934" target="_blank" rel="noreferrer">934</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1006" target="_blank" rel="noreferrer">1006</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0403" target="_blank" rel="noreferrer">403</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0343" target="_blank" rel="noreferrer">343</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0792" target="_blank" rel="noreferrer">792</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0743" target="_blank" rel="noreferrer">743</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0744" target="_blank" rel="noreferrer">744</a>.</p>',8),o=[r];function s(l,c,p,g,d,u){return i(),n("div",null,o)}const m=e(a,[["render",s]]);export{f as __pageData,m as default};
