import{_ as e,o as i,c as r,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 54.1-1133 Continuing education.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-1133.md","filePath":"vacode/54.1-1133.md"}'),a={name:"vacode/54.1-1133.md"},n=t('<h1 id="§-54-1-1133-continuing-education" tabindex="-1">§ 54.1-1133 Continuing education. <a class="header-anchor" href="#§-54-1-1133-continuing-education" aria-label="Permalink to &quot;§ 54.1-1133 Continuing education.&quot;">​</a></h1><p>The Board may establish in the regulations requirements for continuing education as a prerequisite to renewal of any certificate or license issued under this article. The Board shall require evidence of knowledge of code changes as a prerequisite to renewal of any certificate or license issued under this article. In addition, the Board may require continuing education for any individual who is found to be in violation of the statutes or regulations governing the practice of licensed tradesmen or certificate holders issued under this article.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0895" target="_blank" rel="noreferrer">895</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0934" target="_blank" rel="noreferrer">934</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1006" target="_blank" rel="noreferrer">1006</a>; 1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0403" target="_blank" rel="noreferrer">403</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0885" target="_blank" rel="noreferrer">885</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0817" target="_blank" rel="noreferrer">817</a>.</p>',3),o=[n];function c(l,s,d,g,u,f){return i(),r("div",null,o)}const p=e(a,[["render",c]]);export{_ as __pageData,p as default};
