import{_ as e,o as a,c as i,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-1500.1 Board for Hearing Aid Specialists and Opticians; qualifications and terms of members; officers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-1500.1.md","filePath":"vacode/54.1-1500.1.md"}'),s={name:"vacode/54.1-1500.1.md"},r=o('<h1 id="§-54-1-1500-1-board-for-hearing-aid-specialists-and-opticians-qualifications-and-terms-of-members-officers" tabindex="-1">§ 54.1-1500.1 Board for Hearing Aid Specialists and Opticians; qualifications and terms of members; officers. <a class="header-anchor" href="#§-54-1-1500-1-board-for-hearing-aid-specialists-and-opticians-qualifications-and-terms-of-members-officers" aria-label="Permalink to &quot;§ 54.1-1500.1 Board for Hearing Aid Specialists and Opticians; qualifications and terms of members; officers.&quot;">​</a></h1><p>A. The Board for Hearing Aid Specialists and Opticians shall consist of 15 members, as follows: four licensed hearing aid specialists, of which at least one shall be licensed as an audiologist by the Board of Audiology and Speech-Language Pathology, six licensed opticians, one otolaryngologist, one ophthalmologist, and three citizen members.</p><p>B. One of the citizen members shall be a hearing aid user or a person who has a family member who is or has been a hearing aid user. Each hearing aid specialist and the otolaryngologist shall have at least five years of experience in their respective fields immediately prior to appointment. Each of the opticians shall have at least five years of experience prior to appointment and the ophthalmologist shall have practiced ophthalmology for at least five years prior to appointment.</p><p>C. The terms of Board members shall be four years.</p><p>D. The Board shall elect a chairman and vice-chairman from its membership.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>.</p>',6),t=[r];function n(l,c,d,h,p,f){return a(),i("div",null,t)}const _=e(s,[["render",n]]);export{g as __pageData,_ as default};
