import{_ as e,o as i,c as a,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 54.1-1501 Exemptions; sale of hearing aids by corporations, etc., measuring hearing.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-1501.md","filePath":"vacode/54.1-1501.md"}'),r={name:"vacode/54.1-1501.md"},n=o('<h1 id="§-54-1-1501-exemptions-sale-of-hearing-aids-by-corporations-etc-measuring-hearing" tabindex="-1">§ 54.1-1501 Exemptions; sale of hearing aids by corporations, etc., measuring hearing. <a class="header-anchor" href="#§-54-1-1501-exemptions-sale-of-hearing-aids-by-corporations-etc-measuring-hearing" aria-label="Permalink to &quot;§ 54.1-1501 Exemptions; sale of hearing aids by corporations, etc., measuring hearing.&quot;">​</a></h1><p>A. Physicians licensed to practice in Virginia and certified by the American Board of Otolaryngology or eligible for such certification shall not be required to pass an examination as a prerequisite to obtaining a license under this chapter.</p><p>B. Nothing in this chapter shall prohibit a corporation, partnership, trust, association or other like organization maintaining an established business address from engaging in the business of selling or offering for sale hearing aids at retail without a license, provided that it employs only licensed practitioners in the direct sale and fitting of such products.</p><p>C. Nothing in this chapter shall prohibit any person who does not sell hearing aids or accessories or who is not employed by an organization which sells hearing aids or accessories from engaging in the practice of measuring human hearing for the purpose of selection of hearing aids.</p><p>D. Audiologists licensed to practice in Virginia who have earned a doctoral degree in audiology shall not be required to pass an examination as a prerequisite to obtaining a license under this chapter.</p><p>1970, c. 571, §§ 54-524.111, 54-524.112; 1974, c. 534; 1986, c. 279; 1988, c. 765; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0741" target="_blank" rel="noreferrer">741</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0458" target="_blank" rel="noreferrer">458</a>.</p>',6),t=[n];function s(c,h,l,p,g,d){return i(),a("div",null,t)}const _=e(r,[["render",s]]);export{u as __pageData,_ as default};
