import{_ as e,o as i,c as t,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-1506 Exemptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-1506.md","filePath":"vacode/54.1-1506.md"}'),a={name:"vacode/54.1-1506.md"},s=o('<h1 id="§-54-1-1506-exemptions" tabindex="-1">§ 54.1-1506 Exemptions. <a class="header-anchor" href="#§-54-1-1506-exemptions" aria-label="Permalink to &quot;§ 54.1-1506 Exemptions.&quot;">​</a></h1><p>The provisions of this chapter shall not apply to:</p><ol><li><p>Any licensed physician or licensed optometrist;</p></li><li><p>Any individual, partnership, or corporation engaged in supplying ophthalmic prescriptions and supplies exclusively to licensed physicians, licensed optometrists, licensed opticians, or optical scientists;</p></li><li><p>Any person who does not hold himself out to the public as an &quot;optician,&quot; and who works exclusively under the direct supervision and control of a licensed physician or licensed optometrist or licensed optician, and in the same location;</p></li><li><p>The sale of spectacles, eyeglasses, magnifying glasses, goggles, sunglasses, telescopes, or binoculars that are completely preassembled and sold as merchandise; or</p></li><li><p>Any optician who (i) does not regularly practice in Virginia; (ii) holds a current valid license or certificate to practice as an optician in another state, territory, district, or possession of the United States; (iii) volunteers to provide free health care to an underserved area of the Commonwealth under the auspices of a publicly supported all volunteer, nonprofit organization with no paid employees that sponsors the provision of health care to populations of underserved people throughout the world; (iv) files a copy of the license or certificate issued in such other jurisdiction with the Board; (v) notifies the Board, within 15 days prior to the voluntary provision of services of the dates and location of such services; and (vi) acknowledges, in writing, that such licensure exemption shall only be valid, in compliance with the Board&#39;s regulations, during the limited period that such free health care is made available through the volunteer, nonprofit organization on the dates and at the location filed with the Board.</p></li></ol><p>1954, c. 237, §§ 54-398.1, 54-398.3; 1974, c. 534; 1988, c. 765, § 54.1-1701; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0740" target="_blank" rel="noreferrer">740</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>.</p>',4),n=[s];function r(l,c,p,h,d,u){return i(),t("div",null,n)}const v=e(a,[["render",r]]);export{g as __pageData,v as default};
