import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 54.1-202 Monetary penalty; delegation to Director of authority enter consent agreements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-202.md","filePath":"vacode/54.1-202.md"}'),r={name:"vacode/54.1-202.md"},n=a('<h1 id="§-54-1-202-monetary-penalty-delegation-to-director-of-authority-enter-consent-agreements" tabindex="-1">§ 54.1-202 Monetary penalty; delegation to Director of authority enter consent agreements. <a class="header-anchor" href="#§-54-1-202-monetary-penalty-delegation-to-director-of-authority-enter-consent-agreements" aria-label="Permalink to &quot;§ 54.1-202 Monetary penalty; delegation to Director of authority enter consent agreements.&quot;">​</a></h1><p>A. Any person licensed or certified by a regulatory board who violates any statute or regulation pertaining to that regulatory board who is not criminally prosecuted shall be subject to the monetary penalty provided in this section. If a regulatory board determines that a respondent is guilty of the violation complained of, the board shall determine the amount of the monetary penalty for the violation, which shall not exceed $2,500 for each violation. The penalty may be sued for and recovered in the name of the Commonwealth.</p><p>B. Any regulatory board within the Department of Professional and Occupational Regulation may adopt a resolution delegating to the Director the authority to enter into consent agreements on behalf of the regulatory board with regulants of the board. Such resolution shall specify the types of violations to which the delegation applies and the maximum monetary penalty that may be imposed in a consent agreement for each regulatory violation. No delegation of authority pursuant to this subsection shall provide for a monetary penalty over $2,500 per regulatory violation.</p><p>1979, c. 408, § 54-1.22; 1988, c. 765; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0037" target="_blank" rel="noreferrer">37</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0950" target="_blank" rel="noreferrer">950</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0832" target="_blank" rel="noreferrer">832</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0383" target="_blank" rel="noreferrer">383</a>.</p>',4),i=[n];function l(s,h,c,d,p,g){return t(),o("div",null,i)}const u=e(r,[["render",l]]);export{y as __pageData,u as default};
