import{_ as o,o as a,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 54.1-2023 Penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2023.md","filePath":"vacode/54.1-2023.md"}'),i={name:"vacode/54.1-2023.md"},n=e("h1",{id:"§-54-1-2023-penalty",tabindex:"-1"},[t("§ 54.1-2023 Penalty. "),e("a",{class:"header-anchor",href:"#§-54-1-2023-penalty","aria-label":'Permalink to "§ 54.1-2023 Penalty."'},"​")],-1),l=e("p",null,"In addition to the powers vested in the Board, in any action brought under this chapter, if a court finds that a person has willfully engaged in an act or practice in violation of this chapter, the Attorney General, the attorney for the Commonwealth, or the attorney for the locality may recover for the Literary Fund, upon petition to the court, a civil penalty of not more than $10,000 per violation. For purposes of this section, prima facie evidence of a willful violation may be shown when the Attorney General, the attorney for the Commonwealth, or the attorney for the locality notifies the alleged violator by certified mail that an act or practice is a violation of this chapter and the alleged violator, after receipt of the notice, continues to engage in the act or practice.",-1),s=e("p",null,"Violations of this chapter shall constitute separate and distinct offenses. If the acts or activities violating this chapter also violate another provision of law, an action brought under this chapter shall not prohibit or bar any prosecution or proceeding under such other provision or the imposition or any penalties provided for thereby.",-1),c=e("p",null,[t("2010, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0508",target:"_blank",rel:"noreferrer"},"508"),t("; 2012, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0405",target:"_blank",rel:"noreferrer"},"405"),t(".")],-1),h=[n,l,s,c];function p(d,f,u,v,_,y){return a(),r("div",null,h)}co