import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 54.1-2105.2 Cease and desist orders for unlicensed activity; civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2105.2.md","filePath":"vacode/54.1-2105.2.md"}'),i={name:"vacode/54.1-2105.2.md"},n=r('<h1 id="§-54-1-2105-2-cease-and-desist-orders-for-unlicensed-activity-civil-penalty" tabindex="-1">§ 54.1-2105.2 Cease and desist orders for unlicensed activity; civil penalty. <a class="header-anchor" href="#§-54-1-2105-2-cease-and-desist-orders-for-unlicensed-activity-civil-penalty" aria-label="Permalink to &quot;§ 54.1-2105.2 Cease and desist orders for unlicensed activity; civil penalty.&quot;">​</a></h1><p>A. Notwithstanding any other provision of law, the Board may issue an order requiring any person to cease and desist from acting as a real estate broker or salesperson when such person is not licensed by the Board in accordance with this chapter. The order shall be effective upon its entry and shall become final unless such person files an appeal with the Board in accordance with the Administrative Process Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.) within 21 days of the date of entry of the order.</p><p>B. If the person fails to cease and desist the unlicensed activity after entry of an order in accordance with subsection A, the Board may refer the matter for enforcement pursuant to § <a href="http://law.lis.virginia.gov/vacode/54.1-306/" target="_blank" rel="noreferrer">54.1-306</a>.</p><p>C. Any person engaging in unlicensed activity shall be subject to further proceedings before the Board and the Board may impose a civil penalty not to exceed $1,000 for any real estate transaction or the compensation received from any such real estate transaction, whichever is greater. Any penalties collected under this section shall be paid to the Literary Fund after deduction of the administrative costs of the Board in furtherance of this section.</p><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0437" target="_blank" rel="noreferrer">437</a>.</p>',5),s=[n];function o(c,d,l,h,f,p){return t(),a("div",null,s)}const u=e(i,[["render",o]]);export{v as __pageData,u as default};
