import{_ as r,o,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 54.1-2108 Protection of escrow funds, etc., held by broker.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2108.md","filePath":"vacode/54.1-2108.md"}'),s={name:"vacode/54.1-2108.md"},n=e("h1",{id:"§-54-1-2108-protection-of-escrow-funds-etc-held-by-broker",tabindex:"-1"},[t("§ 54.1-2108 Protection of escrow funds, etc., held by broker. "),e("a",{class:"header-anchor",href:"#§-54-1-2108-protection-of-escrow-funds-etc-held-by-broker","aria-label":'Permalink to "§ 54.1-2108 Protection of escrow funds, etc., held by broker."'},"​")],-1),i=e("p",null,"No licensee or any agent of the licensee shall divert or misuse any funds held in escrow or otherwise held by him for another. Where escrow funds or other funds are held by the licensee or his agents and the Real Estate Board or its agents have reason to believe that the licensee is not able to adequately protect the interests of persons involved, or his conduct threatens their interests, the Board shall file a petition with any court of record having equity jurisdiction over the licensee or any of the funds held by him stating the facts upon which it relies. The court may temporarily enjoin further activity by the licensee and take such further action as shall be necessary to conserve, protect and disburse the funds involved, including the appointment of a receiver. If a receiver is appointed his expenses and a reasonable fee as determined by the court shall be paid by the licensee. If the court finds him unable to make such payment, the Board shall determine whether the expenses and fees shall be paid from the Virginia Real Estate Transaction Recovery Fund or from funds received by the Board. Such determination shall be made within thirty days of the Board's receipt of the court-approved receiver invoices. If the court finds that the licensee was without fault and that he is found not to have violated any provisions of this chapter or of the regulations of the Board, then the receiver's expenses and fees shall be paid by the Board. Such payments shall be paid from funds received by the Board.",-1),h=e("p",null,[t("1973, c. 487, § 54-764.5; 1988, c. 765; 1997, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0082",target:"_blank",rel:"noreferrer"},"82"),t("; 1998, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0029",target:"_blank",rel:"noreferrer"},"29"),t(".")],-1),c=[n,i,h];function d(l,f,u,p,b,v){return o(),a("div",null,c)}const _=r(s,[["render",d]]);export{m as __pageData,_ as default};
