import{_ as e,o,c as s,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-2200.2 Board for Professional Soil Scientists, Wetland Professionals, and Geologists; membership; quorum.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2200.2.md","filePath":"vacode/54.1-2200.2.md"}'),i={name:"vacode/54.1-2200.2.md"},n=a('<h1 id="§-54-1-2200-2-board-for-professional-soil-scientists-wetland-professionals-and-geologists-membership-quorum" tabindex="-1">§ 54.1-2200.2 Board for Professional Soil Scientists, Wetland Professionals, and Geologists; membership; quorum. <a class="header-anchor" href="#§-54-1-2200-2-board-for-professional-soil-scientists-wetland-professionals-and-geologists-membership-quorum" aria-label="Permalink to &quot;§ 54.1-2200.2 Board for Professional Soil Scientists, Wetland Professionals, and Geologists; membership; quorum.&quot;">​</a></h1><p>A. Notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/54.1-200/" target="_blank" rel="noreferrer">54.1-200</a>, the Board for Professional Soil Scientists, Wetland Professionals, and Geologists shall be composed of 13 members as follows: three licensed professional soil scientists, three certified professional wetland delineators, three geologists, and three citizen members. The State Geologist shall serve as an ex officio member of the Board. The geologist members shall be of varied backgrounds. The professional soil scientist members shall have experience in at least one of the following areas: (i) soil mapping and classification, (ii) soil suitability and land use, (iii) teaching and research in soil science, and (iv) environmental protection regulations. Of the wetland professional members, one shall have experience in wetland delineation and description, one shall have experience in teaching and research in wetland science, and one shall have experience with natural resource regulations. Terms of the members shall be for four years.</p><p>B. The Board shall annually elect a chairman from its membership. Seven board members, consisting of at least two soil scientists, two professional wetland delineators, two geologists, and one citizen, shall constitute a quorum.</p><p>C. The Governor may select the professional soil scientist members from a list of at least three names for each vacancy submitted by the Virginia Association of Professional Soil Scientists. The Governor may notify the Virginia Association of Professional Soil Scientists of any professional vacancy other than by expiration among the professional soil scientist members of the Board and nominations may be made for the filling of the vacancy.</p><p>D. The Governor may select the wetland professionals from a list of at least three names for each vacancy submitted by the Virginia Association of Wetland Professionals. The Governor may notify and request nominations from the Virginia Association of Wetland Professionals of any professional vacancy other than by expiration among the wetland professional members.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>.</p>',6),t=[n];function r(l,c,f,h,d,m){return o(),s("div",null,t)}const b=e(i,[["render",r]]);export{g as __pageData,b as default};
