import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 54.1-2203 Certification as wetland delineator.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2203.md","filePath":"vacode/54.1-2203.md"}'),r={name:"vacode/54.1-2203.md"},n=i('<h1 id="§-54-1-2203-certification-as-wetland-delineator" tabindex="-1">§ 54.1-2203 Certification as wetland delineator. <a class="header-anchor" href="#§-54-1-2203-certification-as-wetland-delineator" aria-label="Permalink to &quot;§ 54.1-2203 Certification as wetland delineator.&quot;">​</a></h1><p>A. Any person practicing or offering to practice as a wetland professional in the Commonwealth may submit to the Board evidence of qualification to be a certified professional wetland delineator as provided in this chapter. The Board may certify any applicant who has satisfactorily met the requirements of this chapter and its regulations and shall specify on the certificate the appropriate endorsement.</p><p>B. Any individual who allows his certification to lapse by failing to renew the certificate or failing to meet professional activity requirements stipulated in the regulations may be reinstated by the Board upon submission of satisfactory evidence that he is practicing in a competent manner and upon payment of the prescribed fee.</p><p>1987, c. 626, § 54-973; 1988, c. 765; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0784" target="_blank" rel="noreferrer">784</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0777" target="_blank" rel="noreferrer">777</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0859" target="_blank" rel="noreferrer">859</a>.</p>',4),o=[n];function s(c,l,d,p,f,h){return t(),a("div",null,o)}const g=e(r,[["render",s]]);export{m as __pageData,g as default};
