import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 54.1-2205 License required; application; requirements for licensure; continuing education.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2205.md","filePath":"vacode/54.1-2205.md"}'),o={name:"vacode/54.1-2205.md"},n=t('<h1 id="§-54-1-2205-license-required-application-requirements-for-licensure-continuing-education" tabindex="-1">§ 54.1-2205 License required; application; requirements for licensure; continuing education. <a class="header-anchor" href="#§-54-1-2205-license-required-application-requirements-for-licensure-continuing-education" aria-label="Permalink to &quot;§ 54.1-2205 License required; application; requirements for licensure; continuing education.&quot;">​</a></h1><p>A. No person shall engage in, or offer to engage in, the practice of soil evaluation in the Commonwealth unless he has been licensed under the provisions of this chapter.</p><p>B. In order to be licensed as a professional soil scientist, an applicant shall:</p><ol><li>Submit satisfactory evidence verified by affidavits that the applicant:</li></ol><p>a. Is 18 years of age or older;</p><p>b. Is of good moral character; and</p><p>c. Has successfully completed such educational and experiential requirements as are required by this chapter and the regulations of the Board.</p><ol start="2"><li>Achieve a score acceptable to the Board on an examination in the principles and practice of soil evaluation and satisfy one of the following criteria:</li></ol><p>a. Hold a bachelor&#39;s degree from an accredited institution of higher education in a soils curriculum which has been approved by the Board and have at least four years of experience in soil evaluation, the quality of which demonstrates to the Board that the applicant is competent to practice as a professional soil scientist; or</p><p>b. Hold a bachelor&#39;s degree in one of the natural sciences and have at least five years of experience in soil evaluation, the quality of which demonstrates to the Board that the applicant is competent to practice as a professional soil scientist; or</p><p>c. Have a record of at least eight years of experience in soil evaluation, the quality of which demonstrates to the Board that the applicant is competent to practice as a professional soil scientist; or</p><p>d. Have at least four years of experience in soil science research or as a teacher of soils curriculum in an accredited institution of higher education which offers an approved four-year program in soils and at least two years of soil evaluation experience, the quality of which demonstrates to the Board that the applicant is competent to practice as a professional soil scientist.</p><p>C. The Board shall establish by regulation requirements for continuing education as a prerequisite to the maintenance and renewal of a license issued under this chapter, not to exceed eight contact hours per year.</p><p>D. Individuals applying for a license as a professional soil scientist between July 1, 2013, and July 1, 2015, who (i) have been certified as professional soil scientists by the Board or (ii) have achieved a score set by the Board on the examination required by this section shall be licensed by the Board if all other requirements of this chapter or Board regulations have been met, unless an applicant is found by the Board to have engaged in any act that would constitute grounds for disciplinary action.</p><p>1987, c. 626, § 54-975; 1988, c. 765; 1991, c. 181; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0777" target="_blank" rel="noreferrer">777</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0859" target="_blank" rel="noreferrer">859</a>.</p>',15),r=[n];function s(c,l,p,h,d,u){return a(),i("div",null,r)}const g=e(o,[["render",s]]);export{m as __pageData,g as default};
