import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 54.1-2206.2 Requirements for professional wetland delineator certification.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2206.2.md","filePath":"vacode/54.1-2206.2.md"}'),o={name:"vacode/54.1-2206.2.md"},n=i('<h1 id="§-54-1-2206-2-requirements-for-professional-wetland-delineator-certification" tabindex="-1">§ 54.1-2206.2 Requirements for professional wetland delineator certification. <a class="header-anchor" href="#§-54-1-2206-2-requirements-for-professional-wetland-delineator-certification" aria-label="Permalink to &quot;§ 54.1-2206.2 Requirements for professional wetland delineator certification.&quot;">​</a></h1><p>In order to be certified as a professional wetland delineator, an applicant shall achieve a score acceptable to the Board on an examination, which may include a field practicum, in the principles and practice of wetland delineation, provide three written references from wetland professionals with at least one from a certified professional wetland delineator, and satisfy one of the following criteria:</p><ol><li><p>Hold a bachelor&#39;s degree from an accredited institution of higher education in a wetland science, biology, biological engineering, civil and environmental engineering, ecology, soil science, geology, hydrology or any similar biological, physical, natural science or environmental engineering curriculum that has been approved by the Board; have successfully completed a course of instruction, in state and federal wetland delineation methods, that has been approved by the Board; and have at least four years of experience in wetland delineation, the quality of which demonstrates to the Board that the applicant is competent to practice as a certified professional wetland delineator;</p></li><li><p>Have a record of at least six years of experience in wetland delineation, the quality of which demonstrates to the Board that the applicant is competent to practice as a certified professional wetland delineator; or</p></li><li><p>Have a record of at least four years of experience in wetland science research or as a teacher of wetlands curriculum in an accredited institution of higher education, the quality of which demonstrates to the Board that the applicant is competent to practice as a certified professional wetland delineator.</p></li></ol><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0784" target="_blank" rel="noreferrer">784</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0334" target="_blank" rel="noreferrer">334</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0546" target="_blank" rel="noreferrer">546</a>.</p>',4),r=[n];function l(c,s,d,f,p,h){return a(),t("div",null,r)}const g=e(o,[["render",l]]);export{m as __pageData,g as default};
