import{_ as e,o,c as i,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 54.1-2207 Unprofessional conduct.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2207.md","filePath":"vacode/54.1-2207.md"}'),a={name:"vacode/54.1-2207.md"},n=t('<h1 id="§-54-1-2207-unprofessional-conduct" tabindex="-1">§ 54.1-2207 Unprofessional conduct. <a class="header-anchor" href="#§-54-1-2207-unprofessional-conduct" aria-label="Permalink to &quot;§ 54.1-2207 Unprofessional conduct.&quot;">​</a></h1><p>Any professional soil scientist who is licensed or any wetland delineator who is certified, as provided in this chapter, shall be considered guilty of unprofessional conduct and subject to disciplinary action by the Board, if he:</p><ol><li><p>Obtains his certification or license through fraud or deceit;</p></li><li><p>Violates or cooperates with others in violating any provision of this chapter, the Code of Professional Ethics and Conduct or any regulation of the Board;</p></li><li><p>Performs any act likely to deceive, defraud or harm the public;</p></li><li><p>Demonstrates gross negligence, incompetence or misconduct in the practice of soil evaluation or wetland delineation; or</p></li><li><p>Is convicted of a felony.</p></li></ol><p>1987, c. 626; 1988, c. 765; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0784" target="_blank" rel="noreferrer">784</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0777" target="_blank" rel="noreferrer">777</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0859" target="_blank" rel="noreferrer">859</a>.</p>',4),r=[n];function c(s,l,d,p,f,h){return o(),i("div",null,r)}const g=e(a,[["render",c]]);export{u as __pageData,g as default};
