import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 54.1-2301 Board for Waterworks and Wastewater Works Operators and Onsite Sewage System Professionals; membership; terms; duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2301.md","filePath":"vacode/54.1-2301.md"}'),s={name:"vacode/54.1-2301.md"},o=r('<h1 id="§-54-1-2301-board-for-waterworks-and-wastewater-works-operators-and-onsite-sewage-system-professionals-membership-terms-duties" tabindex="-1">§ 54.1-2301 Board for Waterworks and Wastewater Works Operators and Onsite Sewage System Professionals; membership; terms; duties. <a class="header-anchor" href="#§-54-1-2301-board-for-waterworks-and-wastewater-works-operators-and-onsite-sewage-system-professionals-membership-terms-duties" aria-label="Permalink to &quot;§ 54.1-2301 Board for Waterworks and Wastewater Works Operators and Onsite Sewage System Professionals; membership; terms; duties.&quot;">​</a></h1><p>A. The Board for Waterworks and Wastewater Works Operators and Onsite Sewage System Professionals shall consist of 11 members as follows: the Director of the Office of Water Programs of the State Department of Health, or his designee, the Executive Director of the State Water Control Board, or his designee, a currently employed waterworks operator having a valid license of the highest classification issued by the Board, a currently employed wastewater works operator having a valid license of the highest classification issued by the Board, a faculty member of a public institution of higher education in the Commonwealth whose principal field of teaching is management or operation of waterworks or wastewater works, a representative of an owner of a waterworks, a representative of an owner of a wastewater works, a licensed alternative onsite sewage system operator, a licensed alternative onsite sewage system installer, a licensed onsite soil evaluator, and one citizen member. The alternative onsite sewage system operator, alternative onsite sewage system installer, and onsite soil evaluator shall have practiced for at least five consecutive years immediately prior to appointment. No owner shall be represented on the Board by more than one representative or employee operator. The term of Board members shall be four years.</p><p>B. The Board shall examine waterworks and wastewater works operators and issue licenses. The licenses may be issued in specific operator classifications to attest to the competency of an operator to supervise and operate waterworks and wastewater works while protecting the public health, welfare and property and conserving and protecting the water resources of the Commonwealth.</p><p>C. The Board shall establish a program for licensing individuals as onsite soil evaluators, onsite sewage system installers, and onsite sewage system operators.</p><p>D. The Board, in consultation with the Board of Health, shall adopt regulations for the licensure of (i) onsite soil evaluators; (ii) installers of alternative onsite sewage systems, as defined in § <a href="http://law.lis.virginia.gov/vacode/32.1-163/" target="_blank" rel="noreferrer">32.1-163</a>; and (iii) operators of alternative onsite sewage systems, as defined in § <a href="http://law.lis.virginia.gov/vacode/32.1-163/" target="_blank" rel="noreferrer">32.1-163</a>. Such regulations shall include requirements for (a) minimum education and training, including approved training courses; (b) relevant work experience; (c) demonstrated knowledge and skill; (d) application fees to cover the costs of the program, renewal fees, and schedules; (e) the division of onsite soil evaluators into classes, one of which shall be restricted to the design of conventional onsite sewage systems; and (f) other criteria the Board deems necessary.</p><p>E. The Board shall permit any wastewater works operator to sit for the conventional onsite sewage system operator examination.</p><p>1970, c. 768, §§ 54-573.1, 54-573.3; 1981, c. 447; 1988, c. 765; 1989, c. 97; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0892" target="_blank" rel="noreferrer">892</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0924" target="_blank" rel="noreferrer">924</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0067" target="_blank" rel="noreferrer">67</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0731" target="_blank" rel="noreferrer">731</a>.</p>',7),i=[o];function n(l,d,c,h,p,f){return a(),t("div",null,i)}const g=e(s,[["render",n]]);export{m as __pageData,g as default};
