import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 54.1-2312.01 Special interments; definitions; regulations by Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2312.01.md","filePath":"vacode/54.1-2312.01.md"}'),r={name:"vacode/54.1-2312.01.md"},o=i('<h1 id="§-54-1-2312-01-special-interments-definitions-regulations-by-board" tabindex="-1">§ 54.1-2312.01 Special interments; definitions; regulations by Board. <a class="header-anchor" href="#§-54-1-2312-01-special-interments-definitions-regulations-by-board" aria-label="Permalink to &quot;§ 54.1-2312.01 Special interments; definitions; regulations by Board.&quot;">​</a></h1><p>A. A cemetery company may have a section in the cemetery devoted to the interment of human remains and the pets of such deceased humans, provided:</p><ol><li><p>The section of the cemetery property dedicated for this purpose is segregated entirely from the remainder of the cemetery devoted to the interment of human remains;</p></li><li><p>No uncremated pet is interred in the same grave, crypt, or niche as the remains of a human; and</p></li><li><p>The section of the cemetery is clearly marked and advertised as such by the cemetery company.</p></li></ol><p>B. A cemetery company may have a section in the cemetery devoted to the interment of pets, provided:</p><ol><li><p>The section of the cemetery property dedicated for this purpose is segregated entirely from the remainder of the cemetery devoted to the interment of human remains; and</p></li><li><p>The section of the cemetery is clearly marked and advertised as such by the cemetery company.</p></li></ol><p>C. As used in the section, &quot;pet&quot; means the same as that term is defined in § <a href="/vacode/57-39.20/">57-39.20</a>.</p><p>D. The Board shall adopt such regulations as the Board deems appropriate and necessary to implement the provisions of this section. Regulations of the Board shall be in accordance with the Administrative Process Act (§ <a href="/vacode/2.2-4000/">2.2-4000</a> et seq.).</p><p>2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0500" target="_blank" rel="noreferrer">500</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0537" target="_blank" rel="noreferrer">537</a>.</p>',8),n=[o];function s(d,c,h,p,m,l){return t(),a("div",null,n)}const _=e(r,[["render",s]]);export{y as __pageData,_ as default};
