import{_ as e,o as r,c as t,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 54.1-2312 Exemptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2312.md","filePath":"vacode/54.1-2312.md"}'),a={name:"vacode/54.1-2312.md"},i=o('<h1 id="§-54-1-2312-exemptions" tabindex="-1">§ 54.1-2312 Exemptions. <a class="header-anchor" href="#§-54-1-2312-exemptions" aria-label="Permalink to &quot;§ 54.1-2312 Exemptions.&quot;">​</a></h1><p>A. The provisions of this chapter shall not apply to cemeteries wholly owned and operated by the state or a county, city, or town; a church; or a nonstock corporation not operated for profit if the corporation (i) does not compensate any officer or director except for reimbursement of reasonable expenses incurred in the performance of his official duties; (ii) does not sell or construct or directly or indirectly contract for the sale or construction of vaults or lawn, garden, or mausoleum crypts; and (iii) uses proceeds from the sale of all graves and entombment rights for the sole purpose of defraying the direct expenses of maintaining the cemetery. For the purposes of this subsection, &quot;church&quot; includes a church that operates as a historic landmark.</p><p>B. The provisions of this chapter shall not apply to any community cemetery not operated for profit if the cemetery (i) does not compensate any officer or director except for reimbursement of reasonable expenses incurred in the performance of his official duties, and uses the proceeds from the sale of the graves and mausoleum spaces for the sole purpose of defraying the direct expenses of maintaining its facilities or (ii) was chartered by the Commonwealth prior to 1857 A.D.</p><p>C. The provisions of this chapter regarding preneed burial contracts shall not apply to prearranged funeral plans entered into by licensees of the Board of Funeral Directors and Embalmers.</p><p>D. The provisions of the chapter shall not apply to any family cemetery provided that no graves or entombment rights therein are sold or offered for sale to the public.</p><p>E. Subject to the requirements of § <a href="/vacode/54.1-2312.1/">54.1-2312.1</a>, the provisions of this chapter shall not apply to the resale of any interment right in a cemetery in the Commonwealth.</p><p>1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0708" target="_blank" rel="noreferrer">708</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0721" target="_blank" rel="noreferrer">721</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0792" target="_blank" rel="noreferrer">792</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0027" target="_blank" rel="noreferrer">27</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0033" target="_blank" rel="noreferrer">33</a>.</p>',7),n=[i];function s(c,p,l,h,f,d){return r(),t("div",null,n)}const g=e(a,[["render",s]]);export{u as __pageData,g as default};
