import{_ as e,o,c as i,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 54.1-2314 Refusal, suspension or revocation of license or registration.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2314.md","filePath":"vacode/54.1-2314.md"}'),t={name:"vacode/54.1-2314.md"},a=r('<h1 id="§-54-1-2314-refusal-suspension-or-revocation-of-license-or-registration" tabindex="-1">§ 54.1-2314 Refusal, suspension or revocation of license or registration. <a class="header-anchor" href="#§-54-1-2314-refusal-suspension-or-revocation-of-license-or-registration" aria-label="Permalink to &quot;§ 54.1-2314 Refusal, suspension or revocation of license or registration.&quot;">​</a></h1><p>The Board may refuse to license or register any applicant, suspend a license or registration for a stated period or indefinitely, revoke any license or registration, censure or reprimand any person licensed or registered by the Board or place such person on probation for such time as it may designate for any of the following causes related to the sale or offering to the public of cemetery vaults, grave liners, urns, memorials, markers or monuments:</p><ol><li><p>Conviction of any felony or any crime involving moral turpitude;</p></li><li><p>Unprofessional conduct which is likely to defraud or to deceive the public or clients;</p></li><li><p>Misrepresentation or fraud in the conduct of the cemetery company or its sales personnel, or in obtaining or renewing a license or registration;</p></li><li><p>False or misleading advertising;</p></li><li><p>Solicitation in violation of subsection B of § <a href="http://law.lis.virginia.gov/vacode/54.1-2327/" target="_blank" rel="noreferrer">54.1-2327</a>;</p></li><li><p>Direct or indirect payment or offer of payment of a commission to others by the licensee, his sales representatives, agents, or employees for the purpose of securing business;</p></li><li><p>Use of alcohol or drugs to the extent that such use renders him unsafe to practice his licensed or registered activity;</p></li><li><p>Aiding or abetting an unlicensed person to engage in the business of a cemetery company;</p></li><li><p>Using profane, indecent or obscene language within the immediate hearing of the family or relatives of a deceased, whose body has been interred or otherwise disposed of;</p></li><li><p>Violation of any statute, ordinance or regulation affecting the handling, custody, care or transportation of dead human bodies;</p></li><li><p>Failure to comply with subsection A of § <a href="http://law.lis.virginia.gov/vacode/54.1-2327/" target="_blank" rel="noreferrer">54.1-2327</a> and to keep on file an itemized statement of all retail prices and fees charged related to the sale of property or services as required by Board regulations and this chapter;</p></li><li><p>Charging third-party handling fees, which shall not include installation fees; and</p></li><li><p>Refusing to honor the transfer of preneed contract arrangements to another party. However the licensee shall not be responsible for paying additional costs associated with any actual transfer.</p></li></ol><p>1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0708" target="_blank" rel="noreferrer">708</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0721" target="_blank" rel="noreferrer">721</a>.</p>',4),n=[a];function s(l,c,p,d,f,h){return o(),i("div",null,n)}const m=e(t,[["render",s]]);export{u as __pageData,m as default};
