import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 54.1-2316 Certain representations unlawful; perpetual care trust fund required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2316.md","filePath":"vacode/54.1-2316.md"}'),n={name:"vacode/54.1-2316.md"},i=a('<h1 id="§-54-1-2316-certain-representations-unlawful-perpetual-care-trust-fund-required" tabindex="-1">§ 54.1-2316 Certain representations unlawful; perpetual care trust fund required. <a class="header-anchor" href="#§-54-1-2316-certain-representations-unlawful-perpetual-care-trust-fund-required" aria-label="Permalink to &quot;§ 54.1-2316 Certain representations unlawful; perpetual care trust fund required.&quot;">​</a></h1><p>It shall be unlawful to sell or offer for sale in the Commonwealth any grave or entombment right in a cemetery and, in connection therewith, to represent to the public in any manner, express or implied, that the entire cemetery or any grave or entombment right therein will be perpetually cared for, unless adequate provision has been made for the perpetual care of the cemetery and all graves and entombment rights therein as to which such representation has been made.</p><p>Each cemetery company shall establish in a Virginia trust company or trust subsidiary or a federally insured bank or savings institution doing business in the Commonwealth, an irrevocable trust fund in the amount of at least $50,000 before the first lot, parcel of land, burial or entombment right is sold. This fund shall be designated the perpetual care trust fund.</p><p>1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0708" target="_blank" rel="noreferrer">708</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0721" target="_blank" rel="noreferrer">721</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0192" target="_blank" rel="noreferrer">192</a>.</p>',4),o=[i];function s(l,u,c,p,d,h){return r(),t("div",null,o)}const _=e(n,[["render",s]]);export{m as __pageData,_ as default};
