import{_ as e,o as t,c as i,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 54.1-2327 Itemized statement and general price list of burial fees to be furnished; solicitations prohibited.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2327.md","filePath":"vacode/54.1-2327.md"}'),a={name:"vacode/54.1-2327.md"},s=r('<h1 id="§-54-1-2327-itemized-statement-and-general-price-list-of-burial-fees-to-be-furnished-solicitations-prohibited" tabindex="-1">§ 54.1-2327 Itemized statement and general price list of burial fees to be furnished; solicitations prohibited. <a class="header-anchor" href="#§-54-1-2327-itemized-statement-and-general-price-list-of-burial-fees-to-be-furnished-solicitations-prohibited" aria-label="Permalink to &quot;§ 54.1-2327 Itemized statement and general price list of burial fees to be furnished; solicitations prohibited.&quot;">​</a></h1><p>A. Every cemetery company licensed pursuant to the provisions of this chapter shall furnish a written general price list and a written itemized statement of charges in connection with burial services provided by the cemetery company.</p><p>Individuals inquiring in person about burial arrangements or the prices of property or services shall be given the general price list. Upon beginning discussion of burial arrangements or the selection of any property or services, the general price list shall be offered by the cemetery company.</p><p>The itemized statement shall include, but not be limited to, the following charges: burial vaults or other burial receptacles, facilities used, and other professional services used, which shall be set forth in a clear and conspicuous manner.</p><p>The general price list and itemized statement of burial fees shall comply with forms prescribed by Board regulation, which regulations shall promote the purposes of this section.</p><p>B. No cemetery company licensed pursuant to the provisions of this chapter shall make any solicitation at-need or any preneed solicitation using in-person communication by the cemetery company, his agents, assistants or employees, which is false, misleading, or contrary to the stated purpose. However, general advertising and preneed solicitation shall be permitted.</p><p>1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0708" target="_blank" rel="noreferrer">708</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0721" target="_blank" rel="noreferrer">721</a>.</p>',7),n=[s];function o(l,c,p,d,h,m){return t(),i("div",null,n)}const b=e(a,[["render",o]]);export{u as __pageData,b as default};
