import{_ as e,o as i,c as n,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 54.1-2347 Exceptions and exemptions generally.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2347.md","filePath":"vacode/54.1-2347.md"}'),o={name:"vacode/54.1-2347.md"},r=t('<h1 id="§-54-1-2347-exceptions-and-exemptions-generally" tabindex="-1">§ 54.1-2347 Exceptions and exemptions generally. <a class="header-anchor" href="#§-54-1-2347-exceptions-and-exemptions-generally" aria-label="Permalink to &quot;§ 54.1-2347 Exceptions and exemptions generally.&quot;">​</a></h1><p>A. The provisions of this article shall not be construed to prevent or prohibit:</p><ol><li><p>An employee of a duly licensed common interest community manager from providing management services within the scope of the employee&#39;s employment by the duly licensed common interest community manager;</p></li><li><p>An employee of an association from providing management services for that association&#39;s common interest community;</p></li><li><p>A resident of a common interest community acting without compensation from providing management services for that common interest community;</p></li><li><p>A resident of a common interest community from providing bookkeeping, billing, or recordkeeping services for that common interest community for compensation, provided the blanket fidelity bond or employee dishonesty insurance policy maintained by the association insures the association against losses resulting from theft or dishonesty committed by such person;</p></li><li><p>A member of the governing board of an association acting without compensation from providing management services for that association&#39;s common interest community;</p></li><li><p>A person acting as a receiver or trustee in bankruptcy in the performance of his duties as such or any person acting under order of any court from providing management services for a common interest community;</p></li><li><p>A duly licensed attorney-at-law from representing an association or a common interest community manager in any business that constitutes the practice of law;</p></li><li><p>A duly licensed certified public accountant from providing bookkeeping or accounting services to an association or a common interest community manager;</p></li><li><p>A duly licensed real estate broker or agent from selling, leasing, renting, or managing lots within a common interest community; or</p></li><li><p>An association, exchange agent, exchange company, managing agent, or managing entity of a time-share project registered pursuant to the Virginia Real Estate Time-Share Act (§ <a href="/vacode/55.1-2200/">55.1-2200</a> et seq.) from providing management services for such time-share project.</p></li></ol><p>B. A licensee of the Board shall comply with the Board&#39;s regulations, notwithstanding the fact that the licensee would be otherwise exempt from licensure under subsection A. Nothing in this subsection shall be construed to require a person to be licensed in accordance with this article if he would be otherwise exempt from such licensure.</p><p>2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0851" target="_blank" rel="noreferrer">851</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0871" target="_blank" rel="noreferrer">871</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0511" target="_blank" rel="noreferrer">511</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0334" target="_blank" rel="noreferrer">334</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0605" target="_blank" rel="noreferrer">605</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',5),a=[r];function s(c,l,m,p,g,h){return i(),n("div",null,a)}const u=e(o,[["render",s]]);export{f as __pageData,u as default};
