import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 54.1-2350 Annual report; form to accompany resale certificates and disclosure packets.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2350.md","filePath":"vacode/54.1-2350.md"}'),r={name:"vacode/54.1-2350.md"},o=i('<h1 id="§-54-1-2350-annual-report-form-to-accompany-resale-certificates-and-disclosure-packets" tabindex="-1">§ 54.1-2350 Annual report; form to accompany resale certificates and disclosure packets. <a class="header-anchor" href="#§-54-1-2350-annual-report-form-to-accompany-resale-certificates-and-disclosure-packets" aria-label="Permalink to &quot;§ 54.1-2350 Annual report; form to accompany resale certificates and disclosure packets.&quot;">​</a></h1><p>In addition to the provisions of § <a href="/vacode/54.1-2349/">54.1-2349</a>, the Board shall:</p><ol><li><p>Administer the provisions of Article 2 (§ <a href="/vacode/54.1-2354.1/">54.1-2354.1</a> et seq.);</p></li><li><p>Develop and disseminate an association annual report form for use in accordance with §§ <a href="/vacode/55.1-1835/">55.1-1835</a>, <a href="/vacode/55.1-1980/">55.1-1980</a>, and <a href="/vacode/55.1-2182/">55.1-2182</a>; and</p></li><li><p>Develop and disseminate a form to accompany resale certificates required pursuant to § <a href="/vacode/55.1-1990/">55.1-1990</a> and association disclosure packets required pursuant to § <a href="/vacode/55.1-1809/">55.1-1809</a>, which form shall summarize the unique characteristics of common interest communities generally that may affect a prospective purchaser&#39;s decision to purchase a lot or unit located in a common interest community. The form shall include information on the following, which may or may not be applicable to a particular common interest community: (i) the obligation on the part of an owner to pay regular annual or special assessments to the association; (ii) the penalty for failure or refusal to pay such assessments; (iii) the purposes for which such assessments, if any, may be used, including for the construction or maintenance of stormwater management facilities; (iv) the importance the declaration of restrictive covenants or condominium instruments, as applicable, and other governing documents play in association living; (v) limitations on an owner&#39;s ability to rent his lot or unit; (vi) limitations on an owner&#39;s ability to park or store certain types of motor vehicles or boats within the common interest community; (vii) limitations on an owner&#39;s ability to maintain an animal as a pet within the lot or unit, or in common areas or common elements; (viii) architectural guidelines applicable to an owner&#39;s lot or unit; (ix) limitations on an owner&#39;s ability to operate a business within a dwelling unit on a lot or within a unit; (x) the period or length of declarant control; and (xi) that the purchase contract for a lot within an association is a legally binding document once it is signed by the prospective purchaser where the purchaser has not elected to cancel the purchase contract in accordance with law. The form shall also provide that (a) the purchaser remains responsible for his own examination of the materials that constitute the resale certificate or disclosure packet and of any table of contents that may be contained therein; (b) the purchaser shall carefully review the entire resale certificate or disclosure packet; and (c) the contents of the resale certificate or disclosure packet shall control to the extent that there are any inconsistencies between the form and the resale certificate or disclosure packet.</p></li></ol><p>2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0851" target="_blank" rel="noreferrer">851</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0871" target="_blank" rel="noreferrer">871</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0257" target="_blank" rel="noreferrer">257</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0070" target="_blank" rel="noreferrer">70</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0733" target="_blank" rel="noreferrer">733</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0065" target="_blank" rel="noreferrer">65</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0066" target="_blank" rel="noreferrer">66</a>.</p>',4),n=[o];function s(c,l,h,p,f,m){return a(),t("div",null,n)}const g=e(r,[["render",s]]);export{d as __pageData,g as default};
