import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 54.1-2354.3 Common Interest Community Ombudsman; appointment; powers and duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2354.3.md","filePath":"vacode/54.1-2354.3.md"}'),a={name:"vacode/54.1-2354.3.md"},r=n('<h1 id="§-54-1-2354-3-common-interest-community-ombudsman-appointment-powers-and-duties" tabindex="-1">§ 54.1-2354.3 Common Interest Community Ombudsman; appointment; powers and duties. <a class="header-anchor" href="#§-54-1-2354-3-common-interest-community-ombudsman-appointment-powers-and-duties" aria-label="Permalink to &quot;§ 54.1-2354.3 Common Interest Community Ombudsman; appointment; powers and duties.&quot;">​</a></h1><p>A. The Director in accordance with § <a href="/vacode/54.1-303/">54.1-303</a> shall appoint a Common Interest Community Ombudsman (the Ombudsman) and shall establish the Office of the Common Interest Community Ombudsman (the Office). The Ombudsman shall be a member in good standing in the Virginia State Bar. All state agencies shall assist and cooperate with the Office in the performance of its duties under this article.</p><p>B. The Office shall:</p><ol><li><p>Assist members in understanding rights and the processes available to them according to the laws and regulations governing common interest communities and respond to general inquiries;</p></li><li><p>Make available, either separately or through an existing website, information concerning common interest communities and such additional information as may be deemed appropriate;</p></li><li><p>Receive notices of final adverse decisions;</p></li><li><p>Upon request, assist members in understanding the rights and processes available under the laws and regulations governing common interest communities and provide referrals to public and private agencies offering alternative dispute resolution services, with a goal of reducing and resolving conflicts among associations and their members;</p></li><li><p>Ensure that members have access to the services provided through the Office and that the members receive timely responses from the representatives of the Office to the inquiries;</p></li><li><p>Maintain data on inquiries received, types of assistance requested, notices of final adverse decisions received, actions taken, and the disposition of each such matter;</p></li><li><p>Upon request to the Director by (i) any of the standing committees of the General Assembly having jurisdiction over common interest communities or (ii) the Housing Commission, provide to the Director for dissemination to the requesting parties assessments of proposed and existing common interest community laws and other studies of common interest community issues;</p></li><li><p>Monitor changes in federal and state laws relating to common interest communities;</p></li><li><p>Provide information to the Director that will permit the Director to report annually on the activities of the Office of the Common Interest Community Ombudsman to the standing committees of the General Assembly having jurisdiction over common interest communities and to the Housing Commission. The Director&#39;s report shall be filed by December 1 of each year and shall include a summary of significant new developments in federal and state laws relating to common interest communities each year; and</p></li><li><p>Carry out activities as the Board determines to be appropriate.</p></li></ol><p>1993, c. 958, § 55-530; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0222" target="_blank" rel="noreferrer">222</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0463" target="_blank" rel="noreferrer">463</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0816" target="_blank" rel="noreferrer">816</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0851" target="_blank" rel="noreferrer">851</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0871" target="_blank" rel="noreferrer">871</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0059" target="_blank" rel="noreferrer">59</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0208" target="_blank" rel="noreferrer">208</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0481" target="_blank" rel="noreferrer">481</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0797" target="_blank" rel="noreferrer">797</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',5),o=[r];function s(l,m,c,d,p,h){return i(),t("div",null,o)}const u=e(a,[["render",s]]);export{f as __pageData,u as default};
