import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 54.1-2400.7 Practitioners treating other practitioners for certain disorders to make reports; immunity from liability.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2400.7.md","filePath":"vacode/54.1-2400.7.md"}'),o={name:"vacode/54.1-2400.7.md"},a=i('<h1 id="§-54-1-2400-7-practitioners-treating-other-practitioners-for-certain-disorders-to-make-reports-immunity-from-liability" tabindex="-1">§ 54.1-2400.7 Practitioners treating other practitioners for certain disorders to make reports; immunity from liability. <a class="header-anchor" href="#§-54-1-2400-7-practitioners-treating-other-practitioners-for-certain-disorders-to-make-reports-immunity-from-liability" aria-label="Permalink to &quot;§ 54.1-2400.7 Practitioners treating other practitioners for certain disorders to make reports; immunity from liability.&quot;">​</a></h1><p>A. Every practitioner in the Commonwealth who is registered, certified, or licensed by a health regulatory board or who holds a multistate licensure privilege to practice nursing who treats professionally any person registered, certified, or licensed by a health regulatory board or who holds a multistate licensure privilege shall report, unless exempted by subsection C hereof, to the Director of the Department of Health Professions whenever any such health professional is treated for mental disorders, chemical dependency or alcoholism, unless the attending practitioner has determined that there is a reasonable probability that the person being treated is competent to continue in practice or would not constitute danger to himself or to the health and welfare of his patients or the public.</p><p>B. Any person making a report required by this section or testifying in a judicial or administrative proceeding as a result of such report shall be immune from any civil liability alleged to have resulted therefrom unless such person acted in bad faith or with malicious intent.</p><p>C. Medical records or information learned or maintained in connection with an alcohol or drug abuse prevention function that is conducted, regulated, or directly or indirectly assisted by any department or agency of the United States shall be exempt from the reporting requirements of this section to the extent that such reporting is in violation of 42 U.S.C. § 290dd-2 or regulations adopted thereunder.</p><p>Code 1950, § 54-317.2; 1966, c. 166; 1973, c. 529, § 54-317.3; 1974, c. 555; 1977, c. 639; 1978, c. 541; 1986, c. 434; 1988, c. 765, § 54.1-2907; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0937" target="_blank" rel="noreferrer">937</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0980" target="_blank" rel="noreferrer">980</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0049" target="_blank" rel="noreferrer">49</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0064" target="_blank" rel="noreferrer">64</a>.</p>',5),n=[a];function s(l,c,d,h,p,m){return t(),r("div",null,n)}const g=e(o,[["render",s]]);export{u as __pageData,g as default};
