import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 54.1-2400.8 Immunity for reporting.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2400.8.md","filePath":"vacode/54.1-2400.8.md"}'),i={name:"vacode/54.1-2400.8.md"},o=a('<h1 id="§-54-1-2400-8-immunity-for-reporting" tabindex="-1">§ 54.1-2400.8 Immunity for reporting. <a class="header-anchor" href="#§-54-1-2400-8-immunity-for-reporting" aria-label="Permalink to &quot;§ 54.1-2400.8 Immunity for reporting.&quot;">​</a></h1><p>In addition to the immunity for reporting as provided by §§ <a href="http://law.lis.virginia.gov/vacode/54.1-2400.6/" target="_blank" rel="noreferrer">54.1-2400.6</a> and <a href="http://law.lis.virginia.gov/vacode/54.1-2400.7/" target="_blank" rel="noreferrer">54.1-2400.7</a>, any person (i) making a report regarding the conduct or competency of a health care practitioner as required by law or regulation, (ii) making a voluntary report to the appropriate regulatory board or to the Department of Health Professions regarding the unprofessional conduct or competency of any practitioner licensed, certified, or registered by a health regulatory board, or (iii) providing information pursuant to an investigation or testifying in a judicial or administrative proceeding as a result of such reports shall be immune from any civil liability resulting therefrom unless such person acted in bad faith or with malicious intent.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0064" target="_blank" rel="noreferrer">64</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0932" target="_blank" rel="noreferrer">932</a>.</p>',3),n=[o];function s(l,c,p,d,g,f){return r(),t("div",null,n)}const _=e(i,[["render",s]]);export{m as __pageData,_ as default};
