import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 54.1-2404.2 Health care providers; disclosure of records; actions for which an authorization is not required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2404.2.md","filePath":"vacode/54.1-2404.2.md"}'),r={name:"vacode/54.1-2404.2.md"},i=o('<h1 id="§-54-1-2404-2-health-care-providers-disclosure-of-records-actions-for-which-an-authorization-is-not-required" tabindex="-1">§ 54.1-2404.2 Health care providers; disclosure of records; actions for which an authorization is not required. <a class="header-anchor" href="#§-54-1-2404-2-health-care-providers-disclosure-of-records-actions-for-which-an-authorization-is-not-required" aria-label="Permalink to &quot;§ 54.1-2404.2 Health care providers; disclosure of records; actions for which an authorization is not required.&quot;">​</a></h1><p>A. Subject to any limitations set forth in an authorization for the disclosure of health records executed pursuant to § <a href="/vacode/32.1-127.1:03/">32.1-127.1:03</a> and the provisions of subsection F of § <a href="/vacode/32.1-127.1:03/">32.1-127.1:03</a>, every health care provider shall make health records, as defined in § <a href="/vacode/32.1-127.1:03/">32.1-127.1:03</a>, of a patient available to any person designated by a patient in an authorization to disclose health records pursuant to § <a href="/vacode/32.1-127.1:03/">32.1-127.1:03</a> to the same extent that such health records are required to be made available to the patient had the patient requested such health records.</p><p>B. Every health care provider shall allow a spouse, parent, adult child, adult sibling, or other person identified by a patient to make an appointment for medical services on behalf of such patient, regardless of whether such patient has executed an authorization to disclose health records to such person pursuant to § <a href="/vacode/32.1-127.1:03/">32.1-127.1:03</a>; however, such health care provider shall not disclose protected health information to the person making the appointment for medical services on behalf of the patient unless the patient has executed an authorization to disclose health records pursuant to § <a href="/vacode/32.1-127.1:03/">32.1-127.1:03</a> or unless otherwise permitted or required to do so by federal or state law or regulations. Nothing in this subsection shall prevent a health care provider from sharing relevant protected health information related to the patient&#39;s health care or payment with a spouse, parent, adult child, adult sibling, or other person involved in the patient&#39;s health care or payment in accordance with 45 C.F.R. § 164.510.</p><p>2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0784" target="_blank" rel="noreferrer">784</a>.</p>',4),s=[i];function n(h,c,d,l,p,u){return a(),t("div",null,s)}const _=e(r,[["render",n]]);export{v as __pageData,_ as default};
