import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-2405 Transfer of patient records in conjunction with closure, sale, or relocation of practice; notice required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2405.md","filePath":"vacode/54.1-2405.md"}'),i={name:"vacode/54.1-2405.md"},a=o('<h1 id="§-54-1-2405-transfer-of-patient-records-in-conjunction-with-closure-sale-or-relocation-of-practice-notice-required" tabindex="-1">§ 54.1-2405 Transfer of patient records in conjunction with closure, sale, or relocation of practice; notice required. <a class="header-anchor" href="#§-54-1-2405-transfer-of-patient-records-in-conjunction-with-closure-sale-or-relocation-of-practice-notice-required" aria-label="Permalink to &quot;§ 54.1-2405 Transfer of patient records in conjunction with closure, sale, or relocation of practice; notice required.&quot;">​</a></h1><p>A. No person licensed, registered, or certified by one of the health regulatory boards under the Department shall transfer records pertaining to a current patient in conjunction with the closure, sale or relocation of a professional practice until such person has first attempted to notify the patient of the pending transfer, either electronically or by mail, at the patient&#39;s last known address, and by publishing prior notice in a newspaper of general circulation within the provider&#39;s practice area, as specified in § <a href="/vacode/8.01-324/">8.01-324</a>.</p><p>The notice shall specify that, at the written request of the patient or an authorized representative, the records or copies will be sent, within a reasonable time, to any other like-regulated provider of the patient&#39;s choice or provided to the patient pursuant to § <a href="/vacode/32.1-127.1:03/">32.1-127.1:03</a>. The notice shall also disclose whether any charges will be billed by the provider for supplying the patient or the provider chosen by the patient with the originals or copies of the patient&#39;s records. Such charges shall not exceed the actual costs of copying and mailing or delivering the records.</p><p>B. For the purposes of this section:</p><p>&quot;Current patient&quot; means a patient who has had a patient encounter with the provider or his professional practice during the two-year period immediately preceding the date of the record transfer.</p><p>&quot;Relocation of a professional practice&quot; means the moving of a practice located in Virginia from the location at which the records are stored at the time of the notice to another practice site that is located more than 30 miles away or to another practice site that is located in another state or the District of Columbia.</p><p>1992, c. 759; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0912" target="_blank" rel="noreferrer">912</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0917" target="_blank" rel="noreferrer">917</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0053" target="_blank" rel="noreferrer">53</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0073" target="_blank" rel="noreferrer">73</a>.</p>',7),n=[a];function c(s,l,h,p,d,f){return t(),r("div",null,n)}const _=e(i,[["render",c]]);export{g as __pageData,_ as default};
