import{_ as e,o as a,c as r,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-2409.3 Participation of advisory boards in disciplinary proceedings.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2409.3.md","filePath":"vacode/54.1-2409.3.md"}'),o={name:"vacode/54.1-2409.3.md"},t=i('<h1 id="§-54-1-2409-3-participation-of-advisory-boards-in-disciplinary-proceedings" tabindex="-1">§ 54.1-2409.3 Participation of advisory boards in disciplinary proceedings. <a class="header-anchor" href="#§-54-1-2409-3-participation-of-advisory-boards-in-disciplinary-proceedings" aria-label="Permalink to &quot;§ 54.1-2409.3 Participation of advisory boards in disciplinary proceedings.&quot;">​</a></h1><p>Notwithstanding any provision of law to the contrary, whenever a disciplinary proceeding involves a respondent who holds a license or certificate authorizing the practice of a profession represented by a statutorily created advisory board whose members are appointed by the Governor, a member of such advisory board shall sit as a full voting member on any special conference committee, informal fact-finding panel, or formal hearing panel pursuant to Article 3 (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4018/" target="_blank" rel="noreferrer">2.2-4018</a> et seq.) of Chapter 40 of Title 2.2 and § <a href="http://law.lis.virginia.gov/vacode/54.1-2400/" target="_blank" rel="noreferrer">54.1-2400</a> or <a href="http://law.lis.virginia.gov/vacode/54.1-2408.2/" target="_blank" rel="noreferrer">54.1-2408.2</a>.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0698" target="_blank" rel="noreferrer">698</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0144" target="_blank" rel="noreferrer">144</a>.</p>',3),n=[t];function s(c,l,d,p,f,_){return a(),r("div",null,n)}const v=e(o,[["render",s]]);export{g as __pageData,v as default};
