import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-2409.4 Authority to receive laboratory results directly.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2409.4.md","filePath":"vacode/54.1-2409.4.md"}'),a={name:"vacode/54.1-2409.4.md"},i=o('<h1 id="§-54-1-2409-4-authority-to-receive-laboratory-results-directly" tabindex="-1">§ 54.1-2409.4 Authority to receive laboratory results directly. <a class="header-anchor" href="#§-54-1-2409-4-authority-to-receive-laboratory-results-directly" aria-label="Permalink to &quot;§ 54.1-2409.4 Authority to receive laboratory results directly.&quot;">​</a></h1><p>A. Any health care practitioner licensed under this title who, within the scope of his practice, orders a laboratory test or other examination of the physical condition of any person shall, if so requested by the patient or his legal guardian, provide a copy of the report of the results to the patient or his legal guardian, unless, in the professional opinion of the health care practitioner, there is a medical reason not to do so.</p><p>B. The health care practitioner, at his sole discretion, may authorize the laboratory to provide a copy of the report of the results directly to the patient or his legal guardian. The patient or his legal guardian shall then be considered authorized to receive the report or result for the purposes of the federal Clinical Laboratory Improvement Amendments.</p><p>C. With the prior authorization of the patient, a laboratory may, contemporaneously with, or subsequent to, furnishing the report to the ordering health care practitioner, provide a copy of the report of the results directly to the insurance carrier, health maintenance organization, or self-insured plan that provides health insurance or similar coverage to the patient. The insurance carrier, health maintenance organization, or self-insured plan shall then be considered authorized to receive the report or result for the purposes of the federal Clinical Laboratory Improvement Amendments.</p><p>2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0887" target="_blank" rel="noreferrer">887</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0930" target="_blank" rel="noreferrer">930</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0807" target="_blank" rel="noreferrer">807</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0849" target="_blank" rel="noreferrer">849</a>.</p>',5),n=[i];function l(s,h,c,p,d,u){return t(),r("div",null,n)}const _=e(a,[["render",l]]);export{g as __pageData,_ as default};
