import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 54.1-2413 Additional conditions related to practitioner-investors.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2413.md","filePath":"vacode/54.1-2413.md"}'),r={name:"vacode/54.1-2413.md"},n=a('<h1 id="§-54-1-2413-additional-conditions-related-to-practitioner-investors" tabindex="-1">§ 54.1-2413 Additional conditions related to practitioner-investors. <a class="header-anchor" href="#§-54-1-2413-additional-conditions-related-to-practitioner-investors" aria-label="Permalink to &quot;§ 54.1-2413 Additional conditions related to practitioner-investors.&quot;">​</a></h1><p>A. No hospital licensed in the Commonwealth shall discriminate against or otherwise penalize any practitioner for compliance with the provisions of this chapter.</p><p>B. No practitioner, other health care worker, or entity shall enter into any agreement, arrangement, or scheme intended to evade the provisions of this chapter by inducing patient referrals in a manner which would be prohibited by this chapter if the practitioner made the referrals directly.</p><p>C. No group practice shall be formed for the purpose of facilitating referrals that would otherwise be prohibited by this chapter.</p><p>D. Notwithstanding the provisions of this chapter, a practitioner may refer a patient who is a member of a health maintenance organization to an entity in which the practitioner is an investor if the referral is made pursuant to a contract with the health maintenance organization.</p><p>E. Notwithstanding the provisions of this chapter, a referral to an entity in which the referring practitioner or his immediate family member is an investor shall not be in violation of this chapter if (i) the health service to be provided is a designated health service as defined in 42 U.S.C. § 1395nn (h)(6), as amended, and an exception authorized by 42 U.S.C. § 1395nn, as amended, or any regulations adopted pursuant thereto, applies, or (ii) the health service to be provided is not a designated health service as defined in 42 U.S.C. § 1395nn (h)(6), as amended, but would qualify for an exception authorized by 42 U.S.C. § 1395nn, as amended, or any regulations adopted pursuant thereto, if the health service were a designated health service.</p><p>1993, c. 869; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0402" target="_blank" rel="noreferrer">402</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0743" target="_blank" rel="noreferrer">743</a>.</p>',7),o=[n];function s(h,d,c,l,p,f){return t(),i("div",null,o)}const v=e(r,[["render",s]]);export{_ as __pageData,v as default};
