import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const d=JSON.parse(`{"title":"§ 54.1-2520 Program establishment; Director's regulatory authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2520.md","filePath":"vacode/54.1-2520.md"}`),a={name:"vacode/54.1-2520.md"},o=i('<h1 id="§-54-1-2520-program-establishment-director-s-regulatory-authority" tabindex="-1">§ 54.1-2520 Program establishment; Director&#39;s regulatory authority. <a class="header-anchor" href="#§-54-1-2520-program-establishment-director-s-regulatory-authority" aria-label="Permalink to &quot;§ 54.1-2520 Program establishment; Director&#39;s regulatory authority.&quot;">​</a></h1><p>A. The Director shall establish, maintain, and administer an electronic system to monitor the dispensing of covered substances to be known as the Prescription Monitoring Program.</p><p>B. The Director, after consultation with relevant health regulatory boards, shall promulgate, in accordance with the provisions of the Administrative Process Act (§ <a href="/vacode/2.2-4000/">2.2-4000</a> et seq.), such regulations as are necessary to implement the prescription monitoring program as provided in this chapter, including, but not limited to, the establishment of criteria for granting waivers of the reporting requirements set forth in § <a href="/vacode/54.1-2521/">54.1-2521</a>.</p><p>C. The Director may enter into contracts as may be necessary for the implementation and maintenance of the Prescription Monitoring Program.</p><p>D. The Director shall provide dispensers with a basic file layout to enable electronic transmission of the information required in this chapter. For those dispensers unable to transmit the required information electronically, the Director shall provide an alternative means of data transmission.</p><p>E. The Director shall also establish an advisory committee within the Department to assist in the implementation and evaluation of the Prescription Monitoring Program. Such advisory committee shall provide guidance to the Director regarding information disclosed pursuant to subdivision C 9 of § <a href="/vacode/54.1-2523/">54.1-2523</a>.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0481" target="_blank" rel="noreferrer">481</a>; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0637" target="_blank" rel="noreferrer">637</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0678" target="_blank" rel="noreferrer">678</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0664" target="_blank" rel="noreferrer">664</a>; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0410" target="_blank" rel="noreferrer">410</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0568" target="_blank" rel="noreferrer">568</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0185" target="_blank" rel="noreferrer">185</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0379" target="_blank" rel="noreferrer">379</a>.</p>',7),n=[o];function s(l,c,h,g,p,m){return r(),t("div",null,n)}const u=e(a,[["render",s]]);export{d as __pageData,u as default};
