import{_ as e,o as i,c as o,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-2525 Unlawful disclosure of information; disciplinary action authorized; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2525.md","filePath":"vacode/54.1-2525.md"}'),a={name:"vacode/54.1-2525.md"},n=r('<h1 id="§-54-1-2525-unlawful-disclosure-of-information-disciplinary-action-authorized-penalties" tabindex="-1">§ 54.1-2525 Unlawful disclosure of information; disciplinary action authorized; penalties. <a class="header-anchor" href="#§-54-1-2525-unlawful-disclosure-of-information-disciplinary-action-authorized-penalties" aria-label="Permalink to &quot;§ 54.1-2525 Unlawful disclosure of information; disciplinary action authorized; penalties.&quot;">​</a></h1><p>A. It shall be unlawful for any person having access to the confidential information in the possession of the program or any data or reports produced by the program to disclose such confidential information except as provided in this chapter. Any person having access to the confidential information in the possession of the program or any data or reports produced by the program who discloses such confidential information in violation of this chapter shall be guilty of a Class 1 misdemeanor upon conviction.</p><p>B. It shall be unlawful for any person who lawfully receives confidential information from the Prescription Monitoring Program to redisclose or use such confidential information in any way other than the authorized purpose for which the request was made. Any person who lawfully receives information from the Prescription Monitoring Program and discloses such confidential information in violation of this chapter shall be guilty of a Class 1 misdemeanor upon conviction.</p><p>C. Nothing in this section shall prohibit (i) a person who prescribes or dispenses a covered substance to a recipient required to be reported to the program from redisclosing information obtained from the Prescription Monitoring Program to another prescriber or dispenser who has responsibility for treating the recipient or (ii) a person who prescribes a covered substance from placing information obtained from the Prescription Monitoring Program in the recipient&#39;s medical record.</p><p>D. Information obtained from the Prescription Monitoring Program pursuant to subdivision B 6 of § <a href="/vacode/32.1-372/">32.1-372</a> shall become part of the patient&#39;s medical record.</p><p>E. Unauthorized use or disclosure of confidential information received from the Prescription Monitoring Program shall also be grounds for disciplinary action by the relevant health regulatory board.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0481" target="_blank" rel="noreferrer">481</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0812" target="_blank" rel="noreferrer">812</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0309" target="_blank" rel="noreferrer">309</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1066" target="_blank" rel="noreferrer">1066</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1067" target="_blank" rel="noreferrer">1067</a>.</p>',7),t=[n];function s(l,c,p,f,h,d){return i(),o("div",null,t)}const m=e(a,[["render",s]]);export{g as __pageData,m as default};
