import{_ as e,o,c as i,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-2601 Exemptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2601.md","filePath":"vacode/54.1-2601.md"}'),t={name:"vacode/54.1-2601.md"},a=r('<h1 id="§-54-1-2601-exemptions" tabindex="-1">§ 54.1-2601 Exemptions. <a class="header-anchor" href="#§-54-1-2601-exemptions" aria-label="Permalink to &quot;§ 54.1-2601 Exemptions.&quot;">​</a></h1><p>This chapter shall not:</p><ol><li><p>Prevent any person from engaging, individually or through his employees, in activities for which he is licensed or from using appropriate descriptive words, phrases or titles to refer to his services;</p></li><li><p>Prevent any person employed by a federal, state, county or municipal agency, or an educational institution as a speech or hearing specialist or therapist from performing the regular duties of his office or position;</p></li><li><p>Prevent any student, intern or trainee in audiology or speech-language pathology, pursuing a course of study at an accredited institution of higher education, or working in a recognized training center, under the direct supervision of a licensed or certified audiologist or speech-language pathologist, from performing services constituting a part of his supervised course of study;</p></li><li><p>Prevent a licensed audiologist or speech-language pathologist from employing or using the services of unlicensed persons as necessary to assist him in his practice;</p></li><li><p>Authorize any person, unless otherwise licensed to do so, to prepare, order, dispense, alter or repair hearing aids or parts of or attachments to hearing aids for consideration. However, audiologists licensed under this chapter may make earmold impressions and prepare and alter earmolds for clinical use and research;</p></li><li><p>Prevent an audiologist from volunteering to provide free health care to an underserved area of the Commonwealth under the auspices of a publicly supported nonprofit organization that sponsors the provision of health care to populations of underserved people if they do so for a period not exceeding three consecutive days and if the nonprofit organization verifies that the practitioner has a valid, unrestricted license in another state.</p></li></ol><p>1972, c. 181, § 54-83.1:7; 1978, c. 34; 1988, c. 765; 1992, c. 706; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0173" target="_blank" rel="noreferrer">173</a>.</p>',4),s=[a];function n(p,c,l,d,h,u){return o(),i("div",null,s)}const m=e(t,[["render",n]]);export{g as __pageData,m as default};
