import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 54.1-2701 Exemptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2701.md","filePath":"vacode/54.1-2701.md"}'),a={name:"vacode/54.1-2701.md"},r=o('<h1 id="§-54-1-2701-exemptions" tabindex="-1">§ 54.1-2701 Exemptions. <a class="header-anchor" href="#§-54-1-2701-exemptions" aria-label="Permalink to &quot;§ 54.1-2701 Exemptions.&quot;">​</a></h1><p>This chapter shall not:</p><ol><li><p>Apply to a licensed physician or surgeon unless he practices dentistry as a specialty;</p></li><li><p>Apply to a nurse practitioner certified by the Board of Nursing and the Board of Medicine except that intraoral procedures shall be performed only under the direct supervision of a licensed dentist;</p></li><li><p>Apply to a dentist or a dental hygienist of the United States Army, Navy, Coast Guard, Air Force, Public Health Service, or Department of Veterans Affairs;</p></li><li><p>Apply to any dentist of the United States Army, Navy, Coast Guard, or Air Force rendering services voluntarily and without compensation while deemed to be licensed pursuant to § <a href="/vacode/54.1-106/">54.1-106</a>;</p></li><li><p>Apply to any dentist or dental hygienist who (i) does not regularly practice dentistry in Virginia, (ii) holds a current valid license or certificate to practice as a dentist or dental hygienist in another state, territory, district or possession of the United States, (iii) volunteers to provide free health care to an underserved area of the Commonwealth under the auspices of a publicly supported nonprofit organization that sponsors the provision of health care to populations of underserved people, (iv) files a copy of the license or certificate issued in such other jurisdiction with the Board, (v) notifies the Board at least five days prior to the voluntary provision of services of the dates and location of such service, and (vi) acknowledges, in writing, that such licensure exemption shall only be valid, in compliance with the Board&#39;s regulations, during the limited period that such free health care is made available through the nonprofit organization on the dates and at the location filed with the Board. Clauses (iv), (v), and (vi) shall not apply to dentists and dental hygienists volunteering to provide free health care to an underserved area of the Commonwealth under the auspices of a publicly supported nonprofit organization that sponsors the provision of health care to populations of underserved people if they do so for a period not exceeding three consecutive days and if the nonprofit organization verifies that the practitioner has a valid, unrestricted license in another state. The Board may deny the right to practice in Virginia to any dentist or dental hygienist whose license has been previously suspended or revoked, who has been convicted of a felony, or who is otherwise found to be in violation of applicable laws or regulations; or</p></li><li><p>Prevent an office assistant from performing usual secretarial duties or other assistance as set forth in regulations promulgated by the Board.</p></li></ol><p>Code 1950, §§ 54-149 through 54-151, 54-172; 1970, c. 639; 1972, c. 805, § 54-200.23; 1975, c. 479; 1988, c. 765; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0509" target="_blank" rel="noreferrer">509</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0740" target="_blank" rel="noreferrer">740</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0495" target="_blank" rel="noreferrer">495</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0290" target="_blank" rel="noreferrer">290</a>.</p>',4),n=[r];function s(l,d,h,p,c,f){return t(),i("div",null,n)}const g=e(a,[["render",s]]);export{v as __pageData,g as default};
