import{_ as e,o as t,c as i,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-2708.4 Board to adopt regulations related to prescribing of opioids.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2708.4.md","filePath":"vacode/54.1-2708.4.md"}'),o={name:"vacode/54.1-2708.4.md"},a=r('<h1 id="§-54-1-2708-4-board-to-adopt-regulations-related-to-prescribing-of-opioids" tabindex="-1">§ 54.1-2708.4 Board to adopt regulations related to prescribing of opioids. <a class="header-anchor" href="#§-54-1-2708-4-board-to-adopt-regulations-related-to-prescribing-of-opioids" aria-label="Permalink to &quot;§ 54.1-2708.4 Board to adopt regulations related to prescribing of opioids.&quot;">​</a></h1><p>The Board shall adopt regulations for the prescribing of opioids, which shall include guidelines for:</p><ol><li><p>The treatment of acute pain, which shall include (i) requirements for an appropriate patient history and evaluation, (ii) limitations on dosages or day supply of drugs prescribed, (iii) requirements for appropriate documentation in the patient&#39;s health record, and (iv) a requirement that the prescriber request and review information contained in the Prescription Monitoring Program in accordance with § <a href="http://law.lis.virginia.gov/vacode/54.1-2522.1/" target="_blank" rel="noreferrer">54.1-2522.1</a>;</p></li><li><p>The treatment of chronic pain, which shall include, in addition to the requirements for treatment of acute pain set forth in subdivision 1, requirements for (i) development of a treatment plan for the patient, (ii) an agreement for treatment signed by the provider and the patient that includes permission to obtain urine drug screens, and (iii) periodic review of the treatment provided at specific intervals to determine the continued appropriateness of such treatment; and</p></li><li><p>Referral of patients to whom opioids are prescribed for substance abuse counseling or treatment, as appropriate.</p></li></ol><p>2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0291" target="_blank" rel="noreferrer">291</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0682" target="_blank" rel="noreferrer">682</a>.</p>',4),n=[a];function s(p,d,l,c,h,f){return t(),i("div",null,n)}const _=e(o,[["render",s]]);export{g as __pageData,_ as default};
