import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-2711.1 Temporary licenses to persons enrolled in advanced dental education programs; Board regulations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2711.1.md","filePath":"vacode/54.1-2711.1.md"}'),o={name:"vacode/54.1-2711.1.md"},n=r('<h1 id="§-54-1-2711-1-temporary-licenses-to-persons-enrolled-in-advanced-dental-education-programs-board-regulations" tabindex="-1">§ 54.1-2711.1 Temporary licenses to persons enrolled in advanced dental education programs; Board regulations. <a class="header-anchor" href="#§-54-1-2711-1-temporary-licenses-to-persons-enrolled-in-advanced-dental-education-programs-board-regulations" aria-label="Permalink to &quot;§ 54.1-2711.1 Temporary licenses to persons enrolled in advanced dental education programs; Board regulations.&quot;">​</a></h1><p>A. Upon recommendation by the dean of the school of dentistry or the dental program director, the Board may issue a temporary annual license to practice dentistry to persons enrolled in advanced dental education programs and persons serving as dental interns, residents or post-doctoral certificate or degree candidates in hospitals or schools of dentistry that maintain dental intern, residency or post-doctoral programs accredited by the Commission on Dental Accreditation of the American Dental Association. Such license shall expire upon the holder&#39;s graduation, withdrawal or termination from the relevant program.</p><p>B. Temporary licenses issued pursuant to this section shall authorize the licensee to perform patient care activities associated with the program in which he is enrolled that take place only within educational facilities owned or operated by, or affiliated with, the dental school or program. Temporary licenses issued pursuant to this section shall not authorize a licensee to practice dentistry in nonaffiliated clinics or private practice settings.</p><p>C. The Board may prescribe such regulations not in conflict with existing law and require such reports from any hospital or the school of dentistry operating an accredited advanced dental education program in the Commonwealth as may be necessary to carry out the provisions of this section.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0754" target="_blank" rel="noreferrer">754</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0020" target="_blank" rel="noreferrer">20</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0116" target="_blank" rel="noreferrer">116</a>.</p>',5),i=[n];function s(c,l,d,p,h,m){return a(),t("div",null,i)}const f=e(o,[["render",s]]);export{g as __pageData,f as default};
