import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 54.1-2712 Permissible practices.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2712.md","filePath":"vacode/54.1-2712.md"}'),a={name:"vacode/54.1-2712.md"},r=n('<h1 id="§-54-1-2712-permissible-practices" tabindex="-1">§ 54.1-2712 Permissible practices. <a class="header-anchor" href="#§-54-1-2712-permissible-practices" aria-label="Permalink to &quot;§ 54.1-2712 Permissible practices.&quot;">​</a></h1><p>The following activities shall be permissible:</p><ol><li><p>Dental assistants or dental hygienists aiding or assisting licensed dentists, or dental assistants aiding or assisting dental hygienists under the general supervision of a dentist in accordance with regulations promulgated pursuant to § <a href="http://law.lis.virginia.gov/vacode/54.1-2729.01/" target="_blank" rel="noreferrer">54.1-2729.01</a>;</p></li><li><p>The performance of mechanical work on inanimate objects only, for licensed dentists, by any person employed in or operating a dental laboratory;</p></li><li><p>Dental students who are enrolled in accredited D.D.S. or D.M.D. degree programs performing dental operations, under the direction of competent instructors (i) within a dental school or college, dental department of an institution of higher education, or other dental facility within an institution of higher education that is accredited by an accrediting agency recognized by the U.S. Department of Education; (ii) in a dental clinic operated by a nonprofit organization providing indigent care; (iii) in governmental or indigent care clinics in which the student is assigned to practice during his final academic year rotations; (iv) in a private dental office for a limited time during the student&#39;s final academic year when under the direct tutorial supervision of a licensed dentist holding appointment on the dental faculty of the school in which the student is enrolled; or (v) practicing dental hygiene in a private dental office under the direct supervision of a licensed dentist holding appointment on the dental faculty of the school in which the student is enrolled;</p></li><li><p>A licensed dentist from another state or country appearing as a clinician for demonstrating technical procedures before a dental society or organization, convention, or dental college, or performing his duties in connection with a specific case on which he may have been called to the Commonwealth;</p></li><li><p>Dental hygiene students enrolled in an accredited dental hygiene program performing dental hygiene practices as a requisite of the program, under the direction of competent instructors, as defined by regulations of the Board of Dentistry, (i) within a dental hygiene program in a dental school or college, or department thereof, or other dental facility within an institution of higher education that is accredited by an accrediting agency recognized by the U.S. Department of Education; (ii) in a dental clinic operated by a nonprofit organization providing indigent care; (iii) in a governmental or indigent care clinic in which the student is assigned to practice during his final academic year rotations; or (iv) in a private dental office for a limited time during the student&#39;s final academic year when under the direct supervision of a licensed dentist or licensed dental hygienist holding appointment on the dental faculty of the school in which the student is enrolled; and</p></li><li><p>A graduate of an accredited dental program or a graduate of an accredited dental hygiene program engaging in clinical practice under the supervision of a licensed faculty member, but only while participating in a continuing education course offered by a dental program or dental hygiene program accredited by the Commission on Dental Accreditation of the American Dental Association.</p></li></ol><p>Code 1950, § 54-147; 1970, c. 639; 1972, c. 805; 1975, c. 479; 1985, c. 373; 1988, c. 765; 1989, c. 131; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0749" target="_blank" rel="noreferrer">749</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0754" target="_blank" rel="noreferrer">754</a>; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0505" target="_blank" rel="noreferrer">505</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0587" target="_blank" rel="noreferrer">587</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0084" target="_blank" rel="noreferrer">84</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0264" target="_blank" rel="noreferrer">264</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0020" target="_blank" rel="noreferrer">20</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0116" target="_blank" rel="noreferrer">116</a>.</p>',4),o=[r];function c(l,s,d,h,g,p){return i(),t("div",null,o)}const m=e(a,[["render",c]]);export{u as __pageData,m as default};
