import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 54.1-2714 Restricted licenses to teach dentistry for foreign dentists.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2714.md","filePath":"vacode/54.1-2714.md"}'),r={name:"vacode/54.1-2714.md"},n=o('<h1 id="§-54-1-2714-restricted-licenses-to-teach-dentistry-for-foreign-dentists" tabindex="-1">§ 54.1-2714 Restricted licenses to teach dentistry for foreign dentists. <a class="header-anchor" href="#§-54-1-2714-restricted-licenses-to-teach-dentistry-for-foreign-dentists" aria-label="Permalink to &quot;§ 54.1-2714 Restricted licenses to teach dentistry for foreign dentists.&quot;">​</a></h1><p>A. The Board may grant, without examination, a restricted license for a temporary appointment to teach dentistry at a dental school in this Commonwealth to any person who:</p><ol><li><p>Is a resident of a foreign country;</p></li><li><p>Is licensed to practice dentistry in a foreign country;</p></li><li><p>Holds a faculty appointment in a dental school in a foreign country;</p></li><li><p>Is a graduate of a foreign dental school or college or the dental department of a foreign institution of higher education;</p></li><li><p>Is not licensed to practice dentistry in Virginia;</p></li><li><p>Has not failed an examination for a license to practice dentistry in this Commonwealth;</p></li><li><p>Has received a temporary appointment to the faculty of a dental school in this Commonwealth to teach dentistry;</p></li><li><p>Is, in the opinion of the Board, qualified to teach dentistry; and</p></li><li><p>Submits a completed application, the supporting documents the Board deems necessary to determine his qualifications, and the prescribed fee.</p></li></ol><p>B. A restricted license shall entitle the licensee to perform all operations which a person licensed to practice dentistry may perform but only for the purpose of teaching. No person granted a restricted license shall practice dentistry intramurally or privately or receive fees for his services.</p><p>C. A restricted license granted pursuant to this section shall expire 24 months from the date of issuance and may not be renewed or reissued.</p><p>1977, c. 349, § 54-175.2; 1988, c. 765; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0020" target="_blank" rel="noreferrer">20</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0116" target="_blank" rel="noreferrer">116</a>.</p>',6),a=[n];function s(c,l,d,p,h,f){return t(),i("div",null,a)}const g=e(r,[["render",s]]);export{_ as __pageData,g as default};
