import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 54.1-2717 Practice of dentistry by professional business entities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2717.md","filePath":"vacode/54.1-2717.md"}'),o={name:"vacode/54.1-2717.md"},s=a('<h1 id="§-54-1-2717-practice-of-dentistry-by-professional-business-entities" tabindex="-1">§ 54.1-2717 Practice of dentistry by professional business entities. <a class="header-anchor" href="#§-54-1-2717-practice-of-dentistry-by-professional-business-entities" aria-label="Permalink to &quot;§ 54.1-2717 Practice of dentistry by professional business entities.&quot;">​</a></h1><p>A. No corporation shall be formed or foreign corporation domesticated in the Commonwealth for the purpose of practicing dentistry other than a professional corporation as permitted by Chapter 7 (§ <a href="/vacode/13.1-542.1/">13.1-542.1</a> et seq.) of Title 13.1.</p><p>B. No limited liability company shall be organized or foreign limited liability company domesticated in the Commonwealth for the purpose of practicing dentistry other than a professional limited liability company as permitted by Chapter 13 (§ <a href="/vacode/13.1-1100/">13.1-1100</a> et seq.) of Title 13.1.</p><p>C. Notwithstanding the provisions of subsections A and B, dentists licensed pursuant to this chapter may practice as employees of the dental clinics operated as specified in subsection A of § <a href="/vacode/54.1-2715/">54.1-2715</a>.</p><p>Code 1950, § 54-183; 1988, c. 765; 1992, c. 574; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0048" target="_blank" rel="noreferrer">48</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0234" target="_blank" rel="noreferrer">234</a>.</p>',5),r=[s];function n(c,p,l,d,f,h){return t(),i("div",null,r)}const b=e(o,[["render",n]]);export{m as __pageData,b as default};
