import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 54.1-2726.1 Restricted volunteer license for certain dental hygienists.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2726.1.md","filePath":"vacode/54.1-2726.1.md"}'),r={name:"vacode/54.1-2726.1.md"},a=n('<h1 id="§-54-1-2726-1-restricted-volunteer-license-for-certain-dental-hygienists" tabindex="-1">§ 54.1-2726.1 Restricted volunteer license for certain dental hygienists. <a class="header-anchor" href="#§-54-1-2726-1-restricted-volunteer-license-for-certain-dental-hygienists" aria-label="Permalink to &quot;§ 54.1-2726.1 Restricted volunteer license for certain dental hygienists.&quot;">​</a></h1><p>A. The Board may issue a restricted volunteer license to a dental hygienist who:</p><ol><li><p>Held an unrestricted license in Virginia or another state as a licensee in good standing at the time the license expired or became inactive;</p></li><li><p>Is sponsored and supervised by a dentist who holds an unrestricted license in the Commonwealth;</p></li><li><p>Is volunteering for a public health or community free clinic that provides dental services to populations of underserved people;</p></li><li><p>Has fulfilled the Board&#39;s requirement related to knowledge of the laws and regulations governing the practice of dentistry in Virginia;</p></li><li><p>Has not failed a clinical examination within the past five years; and</p></li><li><p>Has had at least five years of clinical practice.</p></li></ol><p>B. A person holding a restricted volunteer license under this section shall:</p><ol><li><p>Only practice in public health or community free clinics that provide dental hygiene services to underserved populations;</p></li><li><p>Only treat patients who have been screened by the approved clinic and are eligible for treatment;</p></li><li><p>Attest on a form provided by the Board that he will not receive remuneration directly or indirectly for providing dental hygiene services; and</p></li><li><p>Not be required to complete continuing education in order to renew such a license.</p></li></ol><p>C. A dental hygienist with a restricted volunteer license issued under this section shall only practice dental hygiene under the direction of a dentist with an unrestricted license in Virginia.</p><p>D. A restricted voluntary license granted pursuant to this section shall expire on the June 30 of the second year after its issuance, or shall terminate when the supervising dentist withdraws his sponsorship. Such license may be renewed annually thereafter as long as the supervising dentist continues to sponsor the licensee.</p><p>E. A dental hygienist holding a restricted volunteer license issued pursuant to this section is subject to the provisions of this chapter, the regulations promulgated under this chapter, and the disciplinary regulations which apply to all dental hygienists practicing in Virginia.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0719" target="_blank" rel="noreferrer">719</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0326" target="_blank" rel="noreferrer">326</a>; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0505" target="_blank" rel="noreferrer">505</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0587" target="_blank" rel="noreferrer">587</a>.</p>',9),s=[a];function l(o,c,d,h,p,u){return i(),t("div",null,s)}const v=e(r,[["render",l]]);export{f as __pageData,v as default};
