import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 54.1-2729.2 Dialysis patient care technician; definition.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2729.2.md","filePath":"vacode/54.1-2729.2.md"}'),n={name:"vacode/54.1-2729.2.md"},r=t('<h1 id="§-54-1-2729-2-dialysis-patient-care-technician-definition" tabindex="-1">§ 54.1-2729.2 Dialysis patient care technician; definition. <a class="header-anchor" href="#§-54-1-2729-2-dialysis-patient-care-technician-definition" aria-label="Permalink to &quot;§ 54.1-2729.2 Dialysis patient care technician; definition.&quot;">​</a></h1><p>&quot;Dialysis patient care technician&quot; or &quot;dialysis care technician&quot; means a person who has obtained certification from an organization approved by the Board of Health Professions to provide, under the supervision of a licensed practitioner of medicine or a registered nurse, direct care to patients undergoing renal dialysis treatments in a Medicare-certified renal dialysis facility. Such direct care may include, but need not be limited to, the administration of heparin, topical needle site anesthetics, dialysis solutions, sterile normal saline solution, and blood volumizers in accordance with the order of a licensed physician, nurse practitioner or physician assistant. However, a person who has completed a training program in dialysis patient care may engage in provisional practice to obtain practical experience in providing direct patient care under direct and immediate supervision in accordance with § <a href="http://law.lis.virginia.gov/vacode/54.1-3408/" target="_blank" rel="noreferrer">54.1-3408</a>, until he has taken and received the results of any examination required by a certifying organization approved by the Board or for 24 months from the date of initial practice, whichever occurs sooner.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0995" target="_blank" rel="noreferrer">995</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0075" target="_blank" rel="noreferrer">75</a>.</p>',3),o=[r];function c(s,d,l,p,h,f){return i(),a("div",null,o)}const m=e(n,[["render",c]]);export{u as __pageData,m as default};
