import{_ as e,o as i,c as t,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 54.1-2729.3 Prohibition on use of title without holding certification; continuing competency requirements; fees; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2729.3.md","filePath":"vacode/54.1-2729.3.md"}'),n={name:"vacode/54.1-2729.3.md"},a=o('<h1 id="§-54-1-2729-3-prohibition-on-use-of-title-without-holding-certification-continuing-competency-requirements-fees-penalty" tabindex="-1">§ 54.1-2729.3 Prohibition on use of title without holding certification; continuing competency requirements; fees; penalty. <a class="header-anchor" href="#§-54-1-2729-3-prohibition-on-use-of-title-without-holding-certification-continuing-competency-requirements-fees-penalty" aria-label="Permalink to &quot;§ 54.1-2729.3 Prohibition on use of title without holding certification; continuing competency requirements; fees; penalty.&quot;">​</a></h1><p>A. No person shall hold himself out to be or advertise or permit to be advertised that he is a dialysis patient care technician or dialysis care technician as defined in this chapter unless such person has obtained certification from an organization approved by the Board of Health Professions as examining candidates for appropriate competency or technical proficiency to perform as dialysis patient care technicians or dialysis care technicians.</p><p>B. The title restrictions provided by this section shall apply to the use of the terms &quot;dialysis patient care technician&quot; and &quot;dialysis care technician&quot; or any other term or combination of terms used alone or in combination with the terms &quot;licensed,&quot; &quot;certified,&quot; or &quot;registered,&quot; as such terms also imply a minimum level of education, training, and competence. A person who is authorized for provisional practice to provide direct patient care while obtaining practical experience shall be identified as a &quot;trainee&quot; while working in a renal dialysis facility.</p><p>C. The Board of Health Professions may require such continuing competency training as it may deem necessary for dialysis patient care technicians or dialysis care technicians.</p><p>D. Any person who willfully violates the provisions of this chapter shall be guilty of a Class 3 misdemeanor.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0995" target="_blank" rel="noreferrer">995</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0075" target="_blank" rel="noreferrer">75</a>.</p>',6),r=[a];function s(c,l,h,p,d,u){return i(),t("div",null,r)}const y=e(n,[["render",s]]);export{m as __pageData,y as default};
