import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 54.1-2808.3 Acceptance of third-party-provided caskets.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2808.3.md","filePath":"vacode/54.1-2808.3.md"}'),i={name:"vacode/54.1-2808.3.md"},s=t('<h1 id="§-54-1-2808-3-acceptance-of-third-party-provided-caskets" tabindex="-1">§ 54.1-2808.3 Acceptance of third-party-provided caskets. <a class="header-anchor" href="#§-54-1-2808-3-acceptance-of-third-party-provided-caskets" aria-label="Permalink to &quot;§ 54.1-2808.3 Acceptance of third-party-provided caskets.&quot;">​</a></h1><p>A. When arrangements for funeral services have been made with a licensed funeral service establishment, funeral service licensees shall accept caskets provided by third parties in accordance with 16 C.F.R. Part 453, Funeral Industry Practices, Federal Trade Commission.</p><p>B. No funeral service establishment or funeral service licensee shall be required to store a casket provided by a third party when preneed arrangements for funeral services have been made.</p><p>C. Any person selling or providing preneed caskets shall be subject to the same preneed requirements as set forth in 16 C.F.R. Part 453, Funeral Industry Practices, Federal Trade Commission, and § <a href="/vacode/54.1-2820/">54.1-2820</a>.</p><p>2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0378" target="_blank" rel="noreferrer">378</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0093" target="_blank" rel="noreferrer">93</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0603" target="_blank" rel="noreferrer">603</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0097" target="_blank" rel="noreferrer">97</a>.</p>',5),n=[s];function c(o,d,l,p,h,f){return r(),a("div",null,n)}const g=e(i,[["render",c]]);export{v as __pageData,g as default};
