import{_ as t,o as n,c as s,z as e,a}from"./chunks/framework.9482e208.js";const z=JSON.parse('{"title":"§ 54.1-2812 Itemized statement and general price list of funeral expenses to be furnished.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2812.md","filePath":"vacode/54.1-2812.md"}'),r={name:"vacode/54.1-2812.md"},o=e("h1",{id:"§-54-1-2812-itemized-statement-and-general-price-list-of-funeral-expenses-to-be-furnished",tabindex:"-1"},[a("§ 54.1-2812 Itemized statement and general price list of funeral expenses to be furnished. "),e("a",{class:"header-anchor",href:"#§-54-1-2812-itemized-statement-and-general-price-list-of-funeral-expenses-to-be-furnished","aria-label":'Permalink to "§ 54.1-2812 Itemized statement and general price list of funeral expenses to be furnished."'},"​")],-1),i=e("p",null,"Every person licensed pursuant to the provisions of this chapter shall furnish a written general price list and a written itemized statement of charges in connection with the care and disposition of the body of a deceased person.",-1),l=e("p",null,"Individuals inquiring in person about funeral arrangements or the prices of funeral goods shall be given the general price list. Upon beginning discussion of funeral arrangements or the selection of any funeral goods or services, the general price list must be offered by the funeral licensee.",-1),d=e("p",null,"The itemized statement shall include, but not be limited to, the following charges: casket, other funeral merchandise, vault or other burial receptacle, facilities used, transportation costs, embalming, preparation of the body, other professional services used and disclosure statements required by the Federal Trade Commission, which shall be set forth in a clear and conspicuous manner.",-1),c=e("p",null,"Further, there shall be included a statement of all anticipated cash advances and expenditures requested by the person contracting for the funeral arrangements and such other items as required by regulation of the Board of Funeral Directors and Embalmers. The statement shall be furnished to the person contracting for funeral arrangements at the time such arrangements are made if the person is present and, if not present, no later than the time of the final disposition of the body.",-1),h=e("p",null,"The general price list and itemized statement of funeral expenses shall comply with forms prescribed by regulation of the Board. All regulations promulgated herewith shall promote the purposes of this section.",-1),f=e("p",null,"1979, c. 8, § 54-260.71:1; 1986, c. 42; 1988, c. 765.",-1),u=[o,i,l,d,c,h,f];function p(m,g,_,b,v,x){return n(),s("div",null,u)}const w=t(r,[["render",p]]);export{z as __pageData,w as default};
