import{_ as e,o as i,c as a,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 54.1-2904 Biennial renewal of licenses; copies; fee; lapsed licenses; reinstatement; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2904.md","filePath":"vacode/54.1-2904.md"}'),t={name:"vacode/54.1-2904.md"},n=r('<h1 id="§-54-1-2904-biennial-renewal-of-licenses-copies-fee-lapsed-licenses-reinstatement-penalties" tabindex="-1">§ 54.1-2904 Biennial renewal of licenses; copies; fee; lapsed licenses; reinstatement; penalties. <a class="header-anchor" href="#§-54-1-2904-biennial-renewal-of-licenses-copies-fee-lapsed-licenses-reinstatement-penalties" aria-label="Permalink to &quot;§ 54.1-2904 Biennial renewal of licenses; copies; fee; lapsed licenses; reinstatement; penalties.&quot;">​</a></h1><p>A. Every license granted under the provisions of this chapter shall be renewed biennially as prescribed by the Board. The Board shall send by mail or electronically notice for renewal of a license to every licensee. Failure to receive such notice shall not excuse any licensee from the requirements of renewal. The person receiving such notice shall furnish the information requested and submit the prescribed renewal fee to the Board. Copies of licenses may be obtained as provided in the Board&#39;s regulations.</p><p>B. Any licensee who allows his license to lapse by failing to renew the license or failing to meet professional activity requirements stipulated in the regulations may be reinstated by the Board upon submission of evidence satisfactory to the Board that he is prepared to resume practice in a competent manner and upon payment of the prescribed fee.</p><p>C. Any person practicing during the time his license has lapsed shall be considered an illegal practitioner and shall be subject to the penalties for violation of this chapter.</p><p>D. When the Board of Health has issued an emergency order pursuant to § <a href="/vacode/32.1-13/">32.1-13</a>, the Board may waive (i) the requirement for submission of a fee for renewal or reinstatement of a license to practice medicine or osteopathic medicine or as a physician assistant or nurse practitioner and (ii) the requirement for submission of evidence satisfactory to the Board that a practitioner whose license was allowed to lapse for failure to meet professional activity requirements has satisfied such requirements and is prepared to resume practice in a competent manner for any person who held a valid, unrestricted, active license to practice such profession within the four-year period immediately prior to the application for renewal or reinstatement of such license.</p><p>Code 1950, § 54-315.1; 1958, c. 161; 1962, c. 128; 1966, c. 657; 1968, c. 674; 1970, c. 69; 1975, c. 508; 1980, c. 157; 1982, c. 606; 1985, c. 303; 1988, c. 765; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0937" target="_blank" rel="noreferrer">937</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0980" target="_blank" rel="noreferrer">980</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0688" target="_blank" rel="noreferrer">688</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0144" target="_blank" rel="noreferrer">144</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0101" target="_blank" rel="noreferrer">101</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0720" target="_blank" rel="noreferrer">720</a>, <a href="https://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0753" target="_blank" rel="noreferrer">753</a>.</p>',6),s=[n];function o(l,c,p,h,f,d){return i(),a("div",null,s)}const g=e(t,[["render",o]]);export{m as __pageData,g as default};
