import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-2910.2 Posting of disciplinary information.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2910.2.md","filePath":"vacode/54.1-2910.2.md"}'),o={name:"vacode/54.1-2910.2.md"},n=a('<h1 id="§-54-1-2910-2-posting-of-disciplinary-information" tabindex="-1">§ 54.1-2910.2 Posting of disciplinary information. <a class="header-anchor" href="#§-54-1-2910-2-posting-of-disciplinary-information" aria-label="Permalink to &quot;§ 54.1-2910.2 Posting of disciplinary information.&quot;">​</a></h1><p>The Board shall post on any department website available to the public all final orders, together with any associated notices, which imposed disciplinary action against licensees of the Board. The Board shall not post notices that have not been adjudicated. Notices and orders entered prior to July 1, 2007, that did not result in disciplinary action by the Board may be removed upon written request of the licensee. Nothing in this section shall be construed to prohibit the inspection and copying of records of disciplinary actions to the extent permitted under the Virginia Freedom of Information Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-3700/" target="_blank" rel="noreferrer">2.2-3700</a> et seq.) and § <a href="http://law.lis.virginia.gov/vacode/54.1-2400.2/" target="_blank" rel="noreferrer">54.1-2400.2</a>.</p><p>2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0861" target="_blank" rel="noreferrer">861</a>.</p>',3),r=[n];function s(c,d,l,p,h,f){return t(),i("div",null,r)}const m=e(o,[["render",s]]);export{g as __pageData,m as default};
