import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 54.1-2912.1 Continued competency and office-based anesthesia requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2912.1.md","filePath":"vacode/54.1-2912.1.md"}'),n={name:"vacode/54.1-2912.1.md"},r=i('<h1 id="§-54-1-2912-1-continued-competency-and-office-based-anesthesia-requirements" tabindex="-1">§ 54.1-2912.1 Continued competency and office-based anesthesia requirements. <a class="header-anchor" href="#§-54-1-2912-1-continued-competency-and-office-based-anesthesia-requirements" aria-label="Permalink to &quot;§ 54.1-2912.1 Continued competency and office-based anesthesia requirements.&quot;">​</a></h1><p>A. The Board shall prescribe by regulation such requirements as may be necessary to ensure continued practitioner competence which may include continuing education, testing, and/or any other requirement.</p><p>B. In promulgating such regulations, the Board shall consider (i) the need to promote ethical practice, (ii) an appropriate standard of care, (iii) patient safety, (iv) application of new medical technology, (v) appropriate communication with patients, and (vi) knowledge of the changing health care system.</p><p>C. The Board may approve persons who provide or accredit such programs in order to accomplish the purposes of this section.</p><p>D. Pursuant to § <a href="http://law.lis.virginia.gov/vacode/54.1-2400/" target="_blank" rel="noreferrer">54.1-2400</a> and its authority to establish the qualifications for registration, certification or licensure that are necessary to ensure competence and integrity to engage in the regulated practice, the Board of Medicine shall promulgate regulations governing the practice of medicine related to the administration of anesthesia in physicians&#39; offices.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0227" target="_blank" rel="noreferrer">227</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0324" target="_blank" rel="noreferrer">324</a>.</p>',6),o=[r];function c(s,d,p,h,l,u){return t(),a("div",null,o)}const g=e(n,[["render",c]]);export{m as __pageData,g as default};
