import{_ as t,o as a,c as n,z as e,a as i}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 54.1-2925 Use of experts in disciplinary proceedings.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2925.md","filePath":"vacode/54.1-2925.md"}'),r={name:"vacode/54.1-2925.md"},o=e("h1",{id:"§-54-1-2925-use-of-experts-in-disciplinary-proceedings",tabindex:"-1"},[i("§ 54.1-2925 Use of experts in disciplinary proceedings. "),e("a",{class:"header-anchor",href:"#§-54-1-2925-use-of-experts-in-disciplinary-proceedings","aria-label":'Permalink to "§ 54.1-2925 Use of experts in disciplinary proceedings."'},"​")],-1),s=e("p",null,"In any disciplinary proceeding conducted pursuant to this chapter, the executive director may contract with an expert or a panel of experts in the various specialties to provide assistance in investigating and evaluating practitioners who may be subject to punitive action. The executive director may select experts for this purpose from lists of specialists to be provided and regularly updated by the appropriate professional societies. Any contract between the executive director and any consulting expert shall provide that the consulting expert shall: (i) be available to work with an investigator from the beginning of the investigation; (ii) receive appropriate compensation for his services; (iii) review and evaluate a completed investigation report in accordance with guidelines established by the Board and the Office of the Attorney General and return it to the Board for action within a specified period of time; and (iv) be available to testify for the Board in any administrative or court proceeding arising from the investigations in which he has participated.",-1),c=e("p",null,"Any expert assisting in any investigation voluntarily or under the contract arrangements described in this section shall be immune from any civil liability or criminal prosecution resulting therefrom unless he acted in bad faith or with malicious intent.",-1),d=e("p",null,[i("1986, c. 434, § 54-318.4; 1988, c. 765; 1996, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0519",target:"_blank",rel:"noreferrer"},"519"),i(".")],-1),p=[o,s,c,d];function l(h,f,u,v,g,m){return a(),n("div",null,p)}const b=t(r,[["render",l]]);export{y as __pageData,b as default};
