import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-2928.2 Board to adopt regulations related to prescribing of opioids and buprenorphine.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2928.2.md","filePath":"vacode/54.1-2928.2.md"}'),o={name:"vacode/54.1-2928.2.md"},n=r('<h1 id="§-54-1-2928-2-board-to-adopt-regulations-related-to-prescribing-of-opioids-and-buprenorphine" tabindex="-1">§ 54.1-2928.2 Board to adopt regulations related to prescribing of opioids and buprenorphine. <a class="header-anchor" href="#§-54-1-2928-2-board-to-adopt-regulations-related-to-prescribing-of-opioids-and-buprenorphine" aria-label="Permalink to &quot;§ 54.1-2928.2 Board to adopt regulations related to prescribing of opioids and buprenorphine.&quot;">​</a></h1><p>The Board shall adopt regulations for the prescribing of opioids and products containing buprenorphine. Such regulations shall include guidelines for:</p><ol><li><p>The treatment of acute pain, which shall include (i) requirements for an appropriate patient history and evaluation, (ii) limitations on dosages or day supply of drugs prescribed, (iii) requirements for appropriate documentation in the patient&#39;s health record, and (iv) a requirement that the prescriber request and review information contained in the Prescription Monitoring Program in accordance with § <a href="http://law.lis.virginia.gov/vacode/54.1-2522.1/" target="_blank" rel="noreferrer">54.1-2522.1</a>;</p></li><li><p>The treatment of chronic pain, which shall include, in addition to the requirements for treatment of acute pain set forth in subdivision 1, requirements for (i) development of a treatment plan for the patient, (ii) an agreement for treatment signed by the provider and the patient that includes permission to obtain urine drug screens, and (iii) periodic review of the treatment provided at specific intervals to determine the continued appropriateness of such treatment; and</p></li><li><p>The use of buprenorphine in the treatment of addiction, including a requirement for referral to or consultation with a provider of substance abuse counseling in conjunction with treatment of opioid dependency with products containing buprenorphine.</p></li></ol><p>2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0291" target="_blank" rel="noreferrer">291</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0682" target="_blank" rel="noreferrer">682</a>.</p>',4),a=[n];function p(d,s,c,l,h,u){return i(),t("div",null,a)}const m=e(o,[["render",p]]);export{g as __pageData,m as default};
