import{_ as n,o as t,c as i,z as e,a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 54.1-2937 Temporary licenses to interns and residents in hospitals and other organizations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-2937.md","filePath":"vacode/54.1-2937.md"}'),r={name:"vacode/54.1-2937.md"},o=e("h1",{id:"§-54-1-2937-temporary-licenses-to-interns-and-residents-in-hospitals-and-other-organizations",tabindex:"-1"},[a("§ 54.1-2937 Temporary licenses to interns and residents in hospitals and other organizations. "),e("a",{class:"header-anchor",href:"#§-54-1-2937-temporary-licenses-to-interns-and-residents-in-hospitals-and-other-organizations","aria-label":'Permalink to "§ 54.1-2937 Temporary licenses to interns and residents in hospitals and other organizations."'},"​")],-1),s=e("p",null,"Upon recommendation by the chief of an approved internship or residency program as defined in this chapter, the Board may issue a temporary annual license to practice medicine, osteopathic medicine, or podiatry to interns and residents in such programs. No such license shall be issued to an intern or resident who has not completed successfully the preliminary academic education required for admission to examinations given by the Board in his particular field of practice. Such license shall expire upon the holder's withdrawal or termination from the internship or residency program. The Board may prescribe such regulations not in conflict with existing law and require such reports from hospitals or other organizations operating an approved graduate medical education program in the Commonwealth as may be necessary to carry out the provisions of this section.",-1),c=e("p",null,[a("1986, c. 307, § 54-311.3; 1987, c. 44; 1988, c. 765; 2015, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0525",target:"_blank",rel:"noreferrer"},"525"),a(".")],-1),d=[o,s,c];function h(l,p,m,u,f,_){return t(),i("div",null,d)}con